import React, { Component } from 'react';
import { connect } from 'react-redux';
import {resetPassword, updatePassword} from '../actions/usersActions';
import InputGroup from '../common/InputGroup';

export class Reset extends Component {
  state = {
    password: '',
    confirm_password: '',
    success: null,
    message: '',
    user: null,
  }
  componentDidMount() {
    this.props.resetPassword(this.props.match.params.token, this.props.history);
  }

  componentWillReceiveProps(nextProps) {
    this.setState({
      user: nextProps.user
    })
    console.log(nextProps);
    if (typeof nextProps.success.success_msg !== 'undefined') {
      this.setState({
        success: true,
        message: nextProps.success.success_msg
      })
    } else {
      this.setState({
        success: false,
        message: nextProps.errors
      })
    }
  }

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  onReset = (e) => {
    e.preventDefault();
    const {user, password, confirm_password} = this.state;
    this.props.updatePassword(user, password, confirm_password);
  }

  render() {
    return (
      <div className="row mb-5 ml-auto mr-auto">
        <div className="col-md-6 m-auto">
          <img src="/img/logo.png" className="mx-auto d-block"
            id="appLogo" alt="logo" width="200" height="200" />

          <div className="card card-body shadow">
            <h1 className="text-center mb-3">
              <i className="fas fa-lock">&nbsp;</i> Reset your password
            </h1>

            <form onSubmit={this.onReset}>

              <InputGroup htmlFor="password"
                label="New password"
                type="password"
                id="password"
                name="password"
                onChange={this.onChange}
                error={
                  this.state.success ? false : this.state.message
                }
                placeholder="Enter new password" />

              <InputGroup htmlFor="password"
                label="Confirm new password"
                type="password"
                id="confirm_password"
                name="confirm_password"
                onChange={this.onChange}
                error={
                  this.state.success ? false : this.state.message
                }
                placeholder="Confirm new password" />

              <button type="submit" id="btn-sign-in" className="btn btn-primary btn-block">Update</button>
            </form>
          </div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  errors: state.errors,
  success: state.success,
  user: state.users.user
})

export default connect(mapStateToProps, {resetPassword, updatePassword})(Reset);
