import React, { Component } from 'react';
import NavBar from '../layouts/NavBar';
import Footer from '../layouts/Footer';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import { connect } from 'react-redux';
import { loadAccounts } from '../actions/accountActions';
import Chart from "react-apexcharts";
import { analysis } from '../actions/dataAnalysis';
import { isNumber } from 'util';

class Evaluate extends Component {
  state = {
    tabIndex: 0,
    selected_account_id: '',
    rendered: false,

    options: {},
    series: [],
    labels: []
  }

  componentDidMount() {
    this.props.loadAccounts();
  }

  componentWillReceiveProps(nextProps) {
    const { accounts} = nextProps;
    const {dataAnalysis } = this.props;

    if (!dataAnalysis.loading) {

    }

    if (!accounts.loading && accounts.accounts.length > 0) {
      this.setState({
        selected_account_id: accounts.accounts[0]._id
      }, () => {
        if (!this.state.rendered) {
          this.setState({ rendered: true}, () => {
            this.props.analysis(this.state.selected_account_id);
          })
        }
      })
    }
  }

  onChange = (e) => {
    this.setState({
      selected_account_id: e.target.value
    }, () => {
      this.props.analysis(this.state.selected_account_id);
    })
  }

  render() {
    const {accounts, dataAnalysis } = this.props;
    let hasPositive = [];
    if (!dataAnalysis.loading){
      hasPositive = dataAnalysis.amounts.filter(amount => amount > 0);
      dataAnalysis.amounts.forEach((amount, i) => {
        dataAnalysis.amounts[i] = Math.abs(amount)
      })
    }
    return (
      <div>
        <NavBar active="evaluate" />

        <div className="container mt-3 mb-5">
          <Tabs defaultFocus={true} selectedIndex={this.state.tabIndex} onSelect={tabIndex => this.setState({ tabIndex })}>
            <TabList>
              <Tab>
                <i className='fas fa-chart-bar'></i><span>&nbsp;&nbsp;</span>
                Expense statistics <span>&nbsp;&nbsp;</span>
                {
                  accounts.loading ? null :
                    <span className="badge badge-secondary badge-pill">{accounts.accounts.length}</span>
                }

              </Tab>
              <Tab>
                <i className='fas fa-calculator'></i><span>&nbsp;&nbsp;</span>
                Investing tools
              </Tab>
            </TabList>

            <TabPanel >
              <br />
              <div id='statistics' className='m-auto'>
                {
                  accounts.loading ? null :
                  (
                    accounts.accounts.length === 0 ?
                    'You currently have no accounts.' : null
                  )
                }
                <div className="form-group row justify-content-start" style={{ 'display': 'flex', 'alignItems': 'center' }}>
                  <label htmlFor="currency" className="col-md-4 col-sm-6 col-xs-12 col-form-label">
                    Select account
                  </label>
                  <div className="form-group col-md-7 col-sm-5 col-xs-12 mt-2">
                    <select className="custom-select" id='account' 
                      onChange={this.onChange} >
                      {
                        !accounts.loading && accounts.accounts.map((account) => 
                          <option key={account._id} value={account._id}>{account.name}</option>
                        )
                      }
                    </select>

                  </div>
                </div>
              </div>

              {
                dataAnalysis.analyzing ?
                  <div className="d-flex justify-content-center mb-4">
                    <div className="spinner-border" role="status">
                      <span className="sr-only">Loading...</span>
                    </div>
                  </div>
                  :
                  <div>
                    <div className="row justify-content-between">
                      <div className="col-md-6 col-sm-12">
                        <div className='card card-header bg-light'>
                          <div className='d-flex'>
                            <div className='mr-auto'>Earned: {!dataAnalysis.loading && isNumber(dataAnalysis.earned) ? dataAnalysis.earned : '' }</div>
                            <div>Expense: {!dataAnalysis.loading && isNumber(dataAnalysis.expense) ? Math.abs(dataAnalysis.expense) : ''}</div>
                          </div>
                        </div>
                        <div className='card border-0 shadow bg-primary text-white pt-3 pb-3'>
                          <div className='m-auto text-center'>
                            <div className='mb-3'>
                              <small className='lead text-muted'>Click on labels to view details</small>
                            </div>
                            <Chart
                              options={{
                                legend: {
                                  position: 'bottom',
                                  labels: {
                                    colors: '#ffffff'
                                  }
                                },
                                labels: [
                                  'earned',
                                  'expense'
                                ],
                                plotOptions: {
                                  pie: {
                                    donut: {
                                      size: '55%',
                                      labels: {
                                        show: true,
                                        name: {
                                          show: true,
                                          color: '#ffffff'
                                        },
                                        value: {
                                          show: true,
                                          color: '#ffffff'
                                        }
                                      }
                                    }
                                  }
                                }
                              }}
                              series={
                                !dataAnalysis.loading && isNumber(dataAnalysis.earned) && isNumber(dataAnalysis.expense) ? [dataAnalysis.earned, Math.abs(dataAnalysis.expense)] : []
                              }
                              type="donut"
                            />
                          </div>
                        </div>
                      </div>

                      <div className="col-md-6 col-sm-12">
                        <br/><br/>
                          <Chart
                            options={{
                              xaxis: {
                                categories: !dataAnalysis.loading ? dataAnalysis.categories : [],
                                labels: {
                                  show: true,
                                  rotate: -45,
                                }
                              },
                              yaxis: {
                                show: false,
                                logarithmic: true,
                                decimalsInFloat: 0,
                                max: hasPositive.length === 0 ? 0 :
                                  (!dataAnalysis.loading ? Math.max(...dataAnalysis.amounts) : 0)
                              },
                              plotOptions: {
                                bar: {
                                  distributed: true
                                }
                              },
                              dataLabels: {
                                enabled: false
                              }
                            }}
                            series={
                              [{
                                name: 'amount',
                                data: !dataAnalysis.loading ? dataAnalysis.amounts : [] 
                              }]
                            }
                            type="bar"
                            width="100%"
                          />                             
                      </div>
                    </div>

                    <Chart 
                      series={
                        [
                          {
                            name: "Series 1",
                            data: [45, 52, 38, 45, 19, 23, 2]
                          }
                        ]
                      }
                      options={{
                        chart: {
                          height: 380,
                          type: "area"
                        },

                        fill: {
                          type: 'gradient',
                          gradient: {
                            shadeIntensity: 1,
                            opacityFrom: 0.7,
                            opacityTo: 0.9,
                            stops: [0, 90, 100]
                          }
                        },
                        xaxis: {
                          categories: [
                            "01 Jan",
                            "02 Jan",
                            "03 Jan",
                            "04 Jan",
                            "05 Jan",
                            "06 Jan",
                            "07 Jan"
                          ]
                        }
                      }}
                    />
                </div>
              }

            </TabPanel>
            <TabPanel >
              <br />
              <div id='invest_tools' className='m-auto'>
                to be updated soon ...
              </div>
            </TabPanel>

          </Tabs>
        </div>

        <Footer />
      </div>
    )
  }
}

const mapStateToProps = state => ({
  accounts: state.accounts,
  dataAnalysis: state.dataAnalysis,
  success: state.success
})

export default connect(mapStateToProps, { loadAccounts, analysis})(Evaluate);
