import { FETCH_USERS, ADD_USER, RESET_USER_PASSWORD } from "../actions/types";

const initialState = {
  users: [],
  user: {},
  success_msg: {}
}

export default function (state = initialState, action) {
  switch (action.type) {
    case FETCH_USERS:
      return {
        ...state,
        users: action.payload
      }
    case ADD_USER:
      return {
        ...state,
        users: [action.payload, ...state.users],
        user: action.payload
      }
    case RESET_USER_PASSWORD:
      return {
        ...state,
        success_msg: action.payload
      }
    default:
      return state;
  }
}
