import React, { Component } from 'react';
import { connect } from 'react-redux';
import { logoutUser} from '../actions/usersActions';
import { withRouter } from 'react-router-dom';

export class NavBar extends Component {
  constructor(props){
    super(props);
    this.state = {
      active: ''
    }
  }

  logOut = (e) => {
    e.preventDefault();
    this.props.logoutUser();
    this.props.history.push('/users/login', {message: 'You are logged out.'});
  }

  onClick = (e) => {
    this.setState({
      active: e.target.id
    }, () => console.log(this.state.active))
  }

  render() {
    //console.log(this.props);
    return (
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container">
          <a className="navbar-brand" href="/">Smart Money</a>
          <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarColor01"
            aria-controls="navbarColor01" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navbarColor01">
            <ul className="navbar-nav mr-auto">
              <li className="nav-item" id="home" onClick={this.onClick}>
                <a className="nav-link" href="/">Home <span className="sr-only">(current)</span></a>
              </li>

              <li className="nav-item" id="about" onClick={this.onClick}>
                <a className="nav-link" href="#">About</a>
              </li>

              <li className="nav-item" id="tools" onClick={this.onClick}>
                <a className="nav-link" href="#">Tools</a>
              </li>

              <li className="nav-item" id="contact" onClick={this.onClick}>
                <a className="nav-link" href="/users/contact">Contact</a>
              </li>

              {
                this.props.auth.isAuthenticated ?
                  <li className="nav-item" id="dashboard" onClick={this.onClick}>
                    <a className="nav-link" href="/users/dashboard">Dashboard</a>
                  </li> : null
              }
            </ul>

            {
              this.props.auth.isAuthenticated ? 
                <div className="navbar-text">
                  <p className="navbar-nav text-info">{this.props.auth.user.name}
                    <span>&nbsp;&nbsp;</span>
                    <a onClick={this.logOut} href="">Logout</a>
                  </p>
                </div> : 
                <div className="navbar-text">
                  <div className="navbar-nav">
                    <div>
                      <a href="/users/login">Login</a>
                      <span>&nbsp;/&nbsp;</span>
                      <a href="/users/register">Register</a>
                    </div>
                  </div>
                </div>
            }

          </div>
        </div>
      </nav>
    )
  }
}

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(mapStateToProps, {logoutUser})(withRouter(NavBar));
