import * as React from 'react';
import {currencyTypes, dateConverter, dateLookup} from '../dictionary/dictionary';
import {
  PagingState,
  IntegratedPaging,
  DataTypeProvider,
  SearchState,
  IntegratedFiltering,
  EditingState,
  SortingState,
  IntegratedSorting
} from '@devexpress/dx-react-grid';

import {
  Grid, Table,
  TableHeaderRow, PagingPanel,
  SearchPanel, Toolbar,
  TableEditRow,
  TableEditColumn,
  TableFixedColumns
} from '@devexpress/dx-react-grid-bootstrap4';


//import "@devexpress/dx-react-grid-bootstrap4/dist/dx-react-grid-bootstrap4.css";

/*
==================================================================
Currency formatter
==================================================================
*/
const PaymentFormatter = (props) => {
  const {value, row} = props;
  const {currency} = row;
  return (
    <b style={{ color: value >= 0 ? 'green' : 'red' }}>
      {value >= 0 ? '+' : '-'}<small>{currency === 'USD' ? currencyTypes.USD : (currency === 'VND' ? currencyTypes.VND : currencyTypes.EUR)}</small>{Math.abs(value)}
    </b>
  );
}

const PaymentTypeProvider = props => {
  return (
    <DataTypeProvider
      formatterComponent={PaymentFormatter}
      {...props}
    />
  );
}

const BalanceFormatter = (props) => {
  const { value, row } = props;
  const { currency } = row;
  return (
    <b style={{ color: value >= 0 ? 'green' : 'red' }}>
      {value >= 0 ? '' : '-'}<small>{currency === 'USD' ? currencyTypes.USD : (currency === 'VND' ? currencyTypes.VND : currencyTypes.EUR)}</small>{Math.abs(value)}
    </b>
  );
}

const BalanceTypeProvider = props => (
  <DataTypeProvider
    formatterComponent={BalanceFormatter}
    {...props}
  />
);

/*
==================================================================
Dates formatter
==================================================================
*/
const DateFormatter = (props) => {
  const { row } = props;
  const { date } = row;
  const split = date.split('/');
  const y = split[0].trim();
  const m = split[1].trim();
  const d = split[2].trim();
  console.log(m);
  return (
    <b style={{color: 'darkblue'}}>
      {y + '/' + String(dateConverter[m]).padStart(2,'0') + '/' + d.padStart(2,'0')}
    </b>
  );
}

const DateTypeProvider = props => (
  <DataTypeProvider
    formatterComponent={DateFormatter}
    {...props}
  />
);

/*
==================================================================
Table styles
==================================================================
*/
const TableComponent = ({ ...restProps }) => (
  <Table.Table
    {...restProps}
    className="table-striped"
  />
);

const getRowId = row => {return row.id};


/**
 * ==================================================================
 * Command button styles
 * ==================================================================
 */
const CommandButton = ({
  onExecute, icon, text, hint, color,
}) => {
  return (
    <button
      type="button"
      className="btn btn-link boder-0 mr-2"
      style={{ padding: 11 }}
      onClick={(e) => {
        onExecute();
        e.stopPropagation();
      }}
      title={hint}
    >
      <span className={color || 'undefined'}>
        {icon ? <i className={`fas fa-${icon}`} /> : null}
        {text}
      </span>
    </button>
  );
}

const AddButton = ({ onExecute }) => (
  <CommandButton icon="plus" hint="Create new row" onExecute={onExecute} />
);

const EditButton = ({ onExecute }) => (
  <CommandButton icon="pen" hint="Edit row" color="text-warning" onExecute={onExecute} />
);

const DeleteButton = ({ onExecute }) => (
  <CommandButton
    icon="trash"
    hint="Delete row"
    color="text-danger"
    onExecute={() => {
      // eslint-disable-next-line
      if (window.confirm('Are you sure you want to delete this row?')) {
        onExecute();
      }
    }}
  />
);

const CommitButton = ({ onExecute }) => (
  <CommandButton icon="check" hint="Save changes" color="text-success" onExecute={onExecute} />
);

const CancelButton = ({ onExecute }) => (
  <CommandButton icon="times" hint="Cancel changes" color="text-danger" onExecute={onExecute} />
);

const commandComponents = {
  add: AddButton,
  edit: EditButton,
  delete: DeleteButton,
  commit: CommitButton,
  cancel: CancelButton,
};

const Command = ({ id, onExecute }) => {
  const ButtonComponent = commandComponents[id];
  return (
    <ButtonComponent
      onExecute={onExecute}
    />
  );
};

/**
 * ===================================================
 * TABLE CELLS
 * ===================================================
 */
const LookupEditCell = ({
  column, selectColumns_values, value, onValueChange,
}) => (
    <td
      style={{
        verticalAlign: 'middle',
        padding: 1,
      }}
    >
      <select
        className="form-control"
        style={{ width: '100%', textAlign: column.align }}
        value={value}
        onChange={e => onValueChange(e.target.value)}
      >
        {selectColumns_values.map(val => (
          <option key={val} value={val}>
            {val}
          </option>
        ))}
      </select>
    </td>
  );

const Cell = (props) => {
  return <Table.Cell {...props} style={{verticalAlign: 'middle'}} />;
};

const selectColumns = {
  currency: Object.keys(currencyTypes)
};

const EditCell = (props) => {
  const { column } = props;
  const selectColumns_values = selectColumns[column.name];
  if (selectColumns_values) {
    return <LookupEditCell {...props} selectColumns_values={selectColumns_values} />;
  }
  return <TableEditRow.Cell {...props} />;
};


