import React from 'react';
import { MDBDataTable } from 'mdbreact';
import { Button, Form, OverlayTrigger, Tooltip} from 'react-bootstrap';

const edit_delete = <Form>
  <OverlayTrigger key='top' placement='top' 
    overlay={
      <Tooltip>
        Edit
      </Tooltip>
    }>
    <Button className='btn btn-primary btn-sm mr-2' type='button' >
      <i className='fas fa-pen'></i>
    </Button>
  </OverlayTrigger>

  <OverlayTrigger key='top' placement='top'
    overlay={
      <Tooltip>
        Delete
      </Tooltip>
    }>
    <Button className='btn btn-warning btn-sm' type='button' >
      <i className='fas fa-trash'></i>
    </Button>
  </OverlayTrigger>
</Form>

const DataTablePage = () => {
  const data = {
    columns: [
      {
        label: 'Date',
        field: 'date',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Amount',
        field: 'amount',
        sort: 'desc',
        width: 120
      },
      {
        label: 'Category',
        field: 'category',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Balance',
        field: 'balance',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Notes',
        field: 'notes',
        width: 350
      },
      {
        label: 'Payee',
        field: 'payee',
        sort: 'asc',
        width: 200
      },
      {
        label: 'Edit/Delete',
        field: 'edit',
        width: 150
      }
    ],
    rows: [
      {
        date: 'April 14, 2019',
        amount: '$450',
        category: 'Bills',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit: edit_delete
      },
      {
        date: 'Jan 15, 2019',
        amount: '$550',
        category: 'Groceries',
        balance: '$1,500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit: edit_delete
      },
      {
        date: 'Aug 16, 2019',
        amount: '$250',
        category: 'Cars',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit: edit_delete
      },
      {
        date: 'Feb 14, 2019',
        amount: '$550',
        category: 'Tuition',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit: edit_delete
      },
      {
        date: 'May 14, 2019',
        amount: '$150',
        category: 'Drinks',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit: edit_delete
      },
    ]
  };

  return (
    <MDBDataTable
      scrollX
      data={data}
    />
  );
}

export default DataTablePage;
