import { DATA_ANALYSIS, DATA_ANALYZING } from '../actions/types';

const initialState = {
  categories: [],
  amounts: [],
  data: {},
  earned: '',
  expense: '',
  analyzing: false
};

export default function (state = initialState, action) {
  switch (action.type) {

    case DATA_ANALYSIS:
      return {
        ...state,
        categories: action.payload.categories,
        amounts: action.payload.amounts,
        data: action.payload.data,
        earned: action.payload.earned,
        expense: action.payload.expense,
        analyzing: false
      }

    case DATA_ANALYZING:
      return {
        ...state,
        analyzing: true
      }

    default:
      return state;
  }
}
