import React, { Component } from 'react';

const InputGroup = ({
  htmlFor, label, type,
  id, name, onChange, placeholder, error
}) => {
  return (
    <div className="form-group">
      <label htmlFor={htmlFor}>{label}</label>
      <input type={type} id={id} name={name} onChange={onChange}
        className="form-control" placeholder={placeholder} />
      {
        error && <small className="form-text text-muted invalid-feedback">{error}</small>
      }

    </div>
  )
}

export default InputGroup;
