import React from 'react';
import { MDBDataTable } from 'mdbreact';
import ButtonTooltip from '../common/ButtonTooltip';

const edit_btn = <ButtonTooltip
  tooltip_text='Edit'
  btn_text=''
  btn_icon={<i className='fas fa-pen'></i>}
  placement='left'
  type='submit'
  onSubmit={this.onAdd_transaction}
  btn_style='btn-outline-secondary' />

const delete_btn = <ButtonTooltip
  tooltip_text={null}
  btn_text='Add new transaction'
  btn_icon={null}
  placement='right'
  type='submit'
  onSubmit={this.onAdd_transaction}
  btn_style='btn-outline-secondary' />

class DataTablePage extends React.Component {
  state = {
    data: {
      columns: [
        {
          label: 'Date',
          field: 'date',
          sort: 'asc',
          width: 150
        },
        {
          label: 'Amount',
          field: 'amount',
          sort: 'desc',
          width: 120
        },
        {
          label: 'Category',
          field: 'category',
          sort: 'asc',
          width: 150
        },
        {
          label: 'Balance',
          field: 'balance',
          sort: 'asc',
          width: 150
        },
        {
          label: 'Notes',
          field: 'notes',
          width: 350
        },
        {
          label: 'Payee',
          field: 'payee',
          sort: 'asc',
          width: 200
        },
        {
          label: 'Edit/Delete',
          field: 'edit',
          width: 150
        }
      ],
      rows: [
        {
          date: 'April 14, 2019',
          amount: '$450',
          category: 'Bills',
          balance: '$500',
          notes: 'Short notes for description',
          payee: 'Trung Vo',
          edit: this.state.edit_btn
        },
        {
          date: 'Jan 15, 2019',
          amount: '$550',
          category: 'Groceries',
          balance: '$1,500',
          notes: 'Short notes for description',
          payee: 'Trung Vo',
          edit: this.state.edit_btn
        },
        {
          date: 'Aug 16, 2019',
          amount: '$250',
          category: 'Cars',
          balance: '$500',
          notes: 'Short notes for description',
          payee: 'Trung Vo',
          edit: this.state.edit_btn
        },
        {
          date: 'Feb 14, 2019',
          amount: '$550',
          category: 'Tuition',
          balance: '$500',
          notes: 'Short notes for description',
          payee: 'Trung Vo',
          edit: this.state.edit_btn
        },
        {
          date: 'May 14, 2019',
          amount: '$150',
          category: 'Drinks',
          balance: '$500',
          notes: 'Short notes for description',
          payee: 'Trung Vo',
          edit: this.state.edit_btn
        },
      ]
    },
    edit_btn: <ButtonTooltip
      tooltip_text='Edit'
      btn_text=''
      btn_icon={<i className='fas fa-pen'></i>}
      placement='left'
      type='submit'
      onSubmit={this.onEdit_transaction}
      btn_style='btn-secondary' />,

    delete_btn: <ButtonTooltip
      tooltip_text='Delete'
      btn_text=''
      btn_icon={<i className='fas fa-trash'></i>}
      placement='left'
      type='submit'
      onSubmit={this.onDelete_transaction}
      btn_style='btn-warning' />
  }

  onEdit_transaction = (e) => {

  }

  onDelete_transaction = (e) => {

  }

  render() {
    const {data} = this.state;
    return (
      <MDBDataTable
        scrollX
        data={data}
      />
    )
  }
}


export default DataTablePage;
