import axios from 'axios';
import * as actions from './types';

// add new account
export const addAccount = (account) => dispatch => {
  axios.post('/accounts/new', account)
    .then(res => {

    })
    .catch(err => {
      dispatch({
        type: actions.GET_ERRORS,
        payload: err.response.data
      })
    })
}
