import React from 'react';
import { MDBDataTable } from 'mdbreact';
import {Button} from 'react-bootstrap';

const DataTablePage = () => {
  const data = {
    columns: [
      {
        label: 'Date',
        field: 'date',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Amount',
        field: 'amount',
        sort: 'desc',
        width: 120
      },
      {
        label: 'Category',
        field: 'category',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Balance',
        field: 'balance',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Notes',
        field: 'notes',
        width: 350
      },
      {
        label: 'Payee',
        field: 'payee',
        sort: 'asc',
        width: 200
      },
      {
        label: 'Edit/Delete',
        field: 'edit',
        width: 150
      }
    ],
    rows: [
      {
        date: 'April 14, 2019',
        amount: '$450',
        category: 'Bills',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit: 
        <div>
            <Button className='btn btn-primary btn-sm'>Edit</Button>
            <Button className='btn btn-primary btn-sm'>Delete</Button>
        </div>
      },
      {
        date: 'Jan 15, 2019',
        amount: '$550',
        category: 'Groceries',
        balance: '$1,500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit:
          <div>
            <Button className='btn btn-primary btn-sm'>Edit</Button>
            <Button className='btn btn-primary btn-sm'>Delete</Button>
          </div>
      },
      {
        date: 'Aug 16, 2019',
        amount: '$250',
        category: 'Cars',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit:
          <div>
            <Button className='btn btn-primary btn-sm'>Edit</Button>
            <Button className='btn btn-primary btn-sm'>Delete</Button>
          </div>
      },
      {
        date: 'Feb 14, 2019',
        amount: '$550',
        category: 'Tuition',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit:
          <div>
            <Button className='btn btn-primary btn-sm'>Edit</Button>
            <Button className='btn btn-primary btn-sm'>Delete</Button>
          </div>
      },
      {
        date: 'May 14, 2019',
        amount: '$150',
        category: 'Drinks',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit:
          <div>
            <Button className='btn btn-primary btn-sm'>Edit</Button>
            <Button className='btn btn-primary btn-sm'>Delete</Button>
          </div>
      },
    ]
  };

  return (
    <MDBDataTable
      scrollX
      data={data}
    />
  );
}

export default DataTablePage;
