import React, { Component } from 'react';
import axios from 'axios';
import InputGroup from '../common/InputGroup';
import {withRouter} from 'react-router-dom';

class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      password1: '',
      password2: ''
    }
  }

  register = (e) => {
    e.preventDefault();

    // POST request
    axios.post('/users/register', {
      name: this.state.name,
      email: this.state.email,
      password1: this.state.password1,
      password2: this.state.password2
    }).then(function (response) {
        if (response.data.errors) {
          // Errors in filling form
          this.props.history.push('/users/register')
        } else {
          // Create new user
          this.props.history.push('/users/login')
        }
      }).catch(function (err) {
        if (err) throw err;
      });
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  render() {
    return (
      <div className="row mb-5 ml-auto mr-auto">
        <div className="col-md-6 m-auto ">
          <img src="/img/logo.png" className="mx-auto d-block"
            id="appLogo" alt="logo"
            width="200" height="200" />

          <div className="card card-body">
            <h1 className="text-center mb-3">
              <i className="fas fa-user-plus"></i> Register
            </h1>
            <form onSubmit={this.register}>
              <InputGroup htmlFor="name"
                    label="Name"
                    type="name"
                    id="name"
                    name="name"
                    onChange={this.onChange}
                    placeholder="Enter full name" />

              <InputGroup htmlFor="email"
                label="Email"
                type="email"
                id="email"
                name="email"
                onChange={this.onChange}
                placeholder="Enter email address" />

              <InputGroup htmlFor="password"
                label="Password"
                type="password"
                id="password1"
                name="password1"
                onChange={this.onChange}
                placeholder="Enter password" />

              <InputGroup htmlFor="password"
                label="Confirm your password"
                type="password"
                id="password2"
                name="password2"
                onChange={this.onChange}
                placeholder="Confirm your password" />

              <button type="submit" id='btn-sign-up' className="btn btn-primary btn-block" >
                Register
              </button>
            </form>

            <p className="lead mt-4">Already have an account? <a href="/users/login">Login</a></p>
            <p>Or</p>

            <a href="/users/auth/google" id="google-button" 
                className="btn btn-block btn-google" data-onsuccess="onSignIn"
                data-theme="dark">
              <i className="fa fa-google-plus fa-fw"></i> Sign in with Google
            </a>
            <a href="#" id="facebook-button" className="btn btn-block btn-facebook">
              <i className="fa fa-facebook fa-fw"></i> Sign in with Facebook
            </a>
          </div>
        </div>
      </div>
    )
  }
}

export default withRouter(Register);
