import React, { Component } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import "react-tabs/style/react-tabs.css";
import AccountDetails from './AccountDetails';
import {connect} from 'react-redux';

export class ManageAccount extends Component {
  state = {
    tabIndex: 0
  }
  render() {
    const { accounts, success} = this.props;
    let content;
    if (accounts.loading) {
      content = <div className="d-flex justify-content-center">
        <div className="spinner-border" role="status">
          <span className="sr-only">Loading...</span>
        </div>
      </div>
    } else {
      content = 
      <div>
        {
          typeof success.delete !== 'undefined' && success.delete !== '' ?
            <div className="alert alert-success alert-dismissible fade show"
              role="alert">
              <button type="button" className="close" data-dismiss="alert">&times;</button>
              {success.delete}
            </div> : null
        }

        <p>You currently have {accounts.accounts.length} accounts.</p>
        <Tabs defaultFocus={true} selectedIndex={this.state.tabIndex} onSelect={tabIndex => this.setState({ tabIndex })}>
          <TabList>
            {
              accounts.accounts.map((account, i) => 
                <Tab key={i}>{account.name}</Tab>
              )
            }
          </TabList>
          {
            accounts.accounts.map((account, i) =>
              <TabPanel key={i} >
                <br/>
                <AccountDetails account={account} />
              </TabPanel>
            )
          }
        </Tabs>
      </div>
    }

    return (
      <div>
        {content}
      </div>
    )
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  success: state.success
});

export default connect(mapStateToProps, null)(ManageAccount);
