import React, { Component } from 'react';

export class Forgot extends Component {
  render() {
    return (
      <div class="row mt-5 mb-5 ml-auto mr-auto">
        <div class="col-md-6 m-auto">
          <img src="/img/logo.png" class="mx-auto d-block mb-5"
            id="appLogo" alt="logo" width="200" height="200" />

          <div class="card card-body">
            <h1 class="text-center mb-3">Reset your password</h1>
            <form action="/users/verify" method="POST">
              <div class="form-group">
                <label for="email">Please enter your email address</label>
                <input type="email" id="email" name="email" class="form-control" placeholder="Enter Email" />
              </div>
              <p class="lead"><a href='/users/login'>Back to log in</a></p>
              <button type="submit" id="btn-sign-in" class="btn btn-primary btn-block">Send</button>
            </form>
          </div>
        </div>
      </div>
    )
  }
}

export default Forgot;
