import React, { Component } from 'react';
import InputGroup from '../common/InputGroup';
import { loginUser } from '../actions/usersActions';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import GoogleSignIn from '../login/GoogleSignIn';
import NavBar from '../layouts/NavBar';
import Footer from '../layouts/Footer';
import AlertMessage from '../common/AlertMessage';

export class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: '',
      message: '',
      success: null,
      err_email: '',
      err_password: ''
    }
  }

  componentDidMount() {
    if (typeof this.props.history.location.state !== 'undefined') {
      if (this.props.history.location.state.success) {
        this.setState({
          message: this.props.history.location.state.success_msg,
          success: null
        })
      } else if (this.props.success.success_msg) { 
        this.setState({
          message: this.props.success.success_msg,
          success: null
        })
      } else if (typeof this.props.history.location.state.message !== 'undefined') {
        this.setState({
          message: this.props.history.location.state.message,
          success: null
        })
      }
    }
    if (this.props.auth.isAuthenticated) {
      this.setState({ success: true })
      this.props.history.push('/users/dashboard');
    }
  }

  componentWillReceiveProps(nextProps) {
    //console.log(nextProps);
    if (nextProps.auth.isAuthenticated) {
      this.setState({ success: true })
      this.props.history.push('/users/dashboard');
    }
    if (nextProps.errors) {
      this.setState({ success: false })
      if (typeof nextProps.errors.email !== 'undefined') {
          this.setState({
            err_email: nextProps.errors.email,
          })
      } else {
        this.setState({
          err_email: '',
        })
      }
      if (typeof nextProps.errors.password !== 'undefined') {
        this.setState({
          err_password: nextProps.errors.password
        })
      } else {
        this.setState({
          err_password: '',
        })
      }
      if (typeof nextProps.errors.userNotExist !== 'undefined') {
        this.setState({ message: nextProps.errors.userNotExist });
      } else if (typeof nextProps.errors.wrongPassword !== 'undefined') {
        this.setState({ message: nextProps.errors.wrongPassword });
      } else {
        this.setState({ message: '' });
      }
    }
  }

  // handle login
  login = (e) => {
    e.preventDefault();
    const userInfo = {
      email: this.state.email,
      password: this.state.password,
    }
    this.props.loginUser(userInfo);
  }

  // save change when user type in their fields
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  render() {
    return (
      <div>
        <NavBar />
        <div className="row mb-5 ml-auto mr-auto" id="loginPage">
          <div className="col-md-6 m-auto">
            <img src="/img/logo.png" className="mx-auto d-block"
              id="appLogo" alt="logo" width="200" height="200" />

            <div className="card card-body shadow">
              <h1 className="text-center mb-3"><i className="fas fa-sign-in-alt"></i> Login</h1>
              {
                this.state.success === null ? (this.state.message.length !== 0 ? 
                  <AlertMessage variant={'success'} message={this.state.message} />
                  : null)
                  : (this.state.message.length !== 0 ? 
                  <AlertMessage variant={'danger'} message={this.state.message} /> : null )
              }

              <form onSubmit={this.login} >
                <InputGroup htmlFor="email"
                  label="Email"
                  type="email"
                  id="email"
                  name="email"
                  required={true}
                  onChange={this.onChange}
                  error={this.state.err_email}
                  placeholder="Enter email address" />

                <InputGroup htmlFor="password"
                  label="Password"
                  type="password"
                  id="password"
                  name="password"
                  required={true}
                  onChange={this.onChange}
                  error={this.state.err_password}
                  placeholder="Enter password" />
                <button type="submit" id="btn-sign-in" className="btn btn-info btn-block">Login</button>
              </form>
              <p className="lead mt-4 text-center lead text-muted">
                Don't have account? <a href="/users/register">Register</a>
              </p>
              <p className="lead text-center lead text-muted">
                Forgot your password? <a href="/users/forgot">Reset</a>
              </p>
              <p className="text-center lead text-muted"> Or sign in with socials</p>

              <GoogleSignIn />
              <a href="#" id="facebook-button" className="btn btn-block btn-facebook">
                <i className="fa fa-facebook fa-fw"></i> Sign in with Facebook
              </a>
            </div>
          </div>
        </div>
        <Footer />
      </div>
    )
  }
}

const mapStateToProps = state => ({
  // users: state.users.users,
  user: state.users.user,
  errors: state.errors,
  auth: state.auth,
  success: state.success
})

export default connect(mapStateToProps, {loginUser})(withRouter(Login));
