import React, {Component} from 'react';
import { MDBTable, MDBTableBody, MDBTableHead } from 'mdbreact';
import ButtonTooltip from '../common/ButtonTooltip';
import { Form } from 'react-bootstrap';

const edit_btn = <ButtonTooltip
  tooltip_text='Edit'
  btn_text=''
  btn_icon={<i className='fas fa-pen'></i>}
  placement='left'
  type='submit'
  btn_style='btn-secondary btn-sm mr-2' />

const delete_btn = <ButtonTooltip
  tooltip_text='Delete'
  btn_text=''
  btn_icon={<i className='fas fa-trash'></i>}
  placement='left'
  type='submit'
  btn_style='btn-warning btn-sm' />


class DataTable extends Component {
  state = {
    columns: ['Date', 'Amount', 'Category', 'Balance', 'Notes', 'Payee', 'Edit/Delete'],
    rows: [
      {
        date: 'April 14, 2019',
        amount: '$450',
        category: 'Bills',
        balance: '$500',
        notes: 'Short notes for descriptionShort notes for descriptionShort notes for descriptionShort notes for description',
        payee: 'Trung Vo',
        edit: <div className='btn-group'>
          <Form onSubmit={this.edit_transaction}>
            {edit_btn}
          </Form>
          <Form onSubmit={this.delete_transaction}>
            {delete_btn}
          </Form>
        </div>
      },
      {
        date: 'Jan 15, 2019',
        amount: '$550',
        category: 'Groceries',
        balance: '$1,500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit: <div className='btn-group'>
          <Form onSubmit={this.edit_transaction}>
            {edit_btn}
          </Form>
          <Form onSubmit={this.delete_transaction}>
            {delete_btn}
          </Form>
        </div>
      },
      {
        date: 'Aug 16, 2019',
        amount: '$250',
        category: 'Cars',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit: <div className='btn-group'>
          <Form onSubmit={this.edit_transaction}>
            {edit_btn}
          </Form>
          <Form onSubmit={this.delete_transaction}>
            {delete_btn}
          </Form>
        </div>
      },
      {
        date: 'Feb 14, 2019',
        amount: '$550',
        category: 'Tuition',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit: <div className='btn-group'>
          <Form onSubmit={this.edit_transaction}>
            {edit_btn}
          </Form>
          <Form onSubmit={this.delete_transaction}>
            {delete_btn}
          </Form>
        </div>
      },
      {
        date: 'May 14, 2019',
        amount: '$150',
        category: 'Drinks',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit: <div className='btn-group'>
          <Form onSubmit={this.edit_transaction}>
            {edit_btn}
          </Form>
          <Form onSubmit={this.delete_transaction}>
            {delete_btn}
          </Form>
        </div>
      },
    ]
  }

  render () {
    const {columns, rows} = this.state;
    return (
      <table className='select-checkbox'>
        <thead >
          <tr>
            <th></th>
            {
              columns.map(column => {
                return (
                  <th>{column}</th>
                )
              })
            }
          </tr>
        </thead>
        <tbody>
            {
              rows.map(row => {
                return (
                  <tr>
                    <td></td>
                    <td>{row.date}</td>
                    <td>{row.amount}</td>
                    <td>{row.category}</td>
                    <td>{row.balance}</td>
                    <td>{row.notes}</td>
                    <td>{row.payee}</td>
                    <td>{row.edit}</td>
                  </tr>
                )
              })
            }

        </tbody>
      </table>
    );
  }
}

export default DataTable;
