import React, { Component } from 'react'
import Chart from "chart.js";

export default class MyChart extends Component {
  state = {
    window_width: window.innerWidth
  }
  chartRef = React.createRef();

  componentDidMount() {
    this.buildChart();
  }

  componentDidUpdate() {
    this.buildChart();
  }

  buildChart = () => {
    const {window_width} = this.state;
    const {type, position, data} = this.props;

    window.addEventListener('resize', () => {
      this.setState({ window_width: window.innerWidth  })
    });

    const myChartRef = this.chartRef.current.getContext("2d");

    new Chart(myChartRef, {
      type: type,
      data: data,
      options: {
        title: {
          text: 'Chart',
          display: true
        },
        responsive: true,
        legend: {
          position: window_width > 945 ? (type === 'pie' || type ==='doughnut' ? 'right' : position) : 'bottom',
          labels: {
            generateLabels: function(chart) {
              var data = chart.data;
              if (data.labels.length && data.datasets.length) {
                return data.labels.map(function (label, i) {
                  var result = {};
                  result['text'] = label + ': ' + data.datasets[0].data[i];
                  result['index'] = i;
                  if (type !== 'line') {
                    result['fillStyle'] = data.datasets[0].backgroundColor[i];                  
                  } else {
                    result['fontColor'] = 'rgb(255, 99, 132)'
                  }
                  return result;
                });
              }
              return [];
            }
          }
        }
      }
    });
  }
  render() {
    const {window_width} = this.state;
    return (
      <div>
        <canvas
          id="myChart" 
          style={{height: window_width < 350 ? '350vh' : '250vh', width: '400vw'}}
          ref={this.chartRef}
        />
      </div>
    )
  }
}
