import React, { Component } from 'react';
import DataTablePage from './DataTablePage';
import ButtonTooltip from '../common/ButtonTooltip';

class AccountDetails extends Component {
  onAdd_transaction = (e) => {
    e.preventDefault();
  }

  render() {
    return (
      <div>
        <h2>
          <i className='fas fa-file-alt'>&nbsp;&nbsp;</i>Transactions history
        </h2><br/>
        <h4>Account 1</h4><br/>

        <div id='transactions-table' 
          className="card card-body text-dark table-hover table-responsive table-sm text-left">
          <DataTablePage />

          <ButtonTooltip 
            tooltip_text=''
            btn_text='Add new transaction'
            btn_icon={null}
            placement='right'
            type='submit'
            onSubmit={this.onAdd_transaction}
            btn_style='btn-outline-secondary'/>
        </div>


        <div className="card" id='transactions-card-table'>
          <div className="card-header bg-dark text-white">
            Date: April 14, 2019 <span>&nbsp;&nbsp;</span>
            <button className='btn btn-secondary btn-sm float-right'>
              <i className='fas fa-plus'>&nbsp;&nbsp;</i>Add
            </button>
          </div>

          <div className="card-body text-dark m-auto table-hover table-responsive table-sm text-right">
            <DataTablePage/>
          </div>

          <div className="card-footer text-dark">
            Current balance: $5,000
          </div>
        </div>

      </div>
    )
  }
}

export default AccountDetails;
