import React, { Component } from 'react';
import PropTypes from 'prop-types';
import InputGroup from '../common/InputGroup';
import {withRouter} from 'react-router-dom';
import {createUser} from '../actions/usersActions';
import { connect } from 'react-redux';
import GoogleSignIn from '../login/GoogleSignIn';
import NavBar from '../layouts/NavBar';
import Footer from '../layouts/Footer';

class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      password1: '',
      password2: '',
      errors: {}
    }
  }

  componentDidMount() {
    if (this.props.auth.isAuthenticated) {
      this.props.history.push('/users/dashboard');
    }
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({ errors: nextProps.errors });
    }
    if (typeof nextProps.auth !== 'undefined' && nextProps.auth.isAuthenticated) {
      this.props.history.push('/users/dashboard');
    }
  }

  // handle register new user
  register = (e) => {
    e.preventDefault();
    const newUser = {
      name: this.state.name,
      email: this.state. email,
      password1: this.state.password1,
      password2: this.state.password2
    }
    this.props.createUser(newUser, this.props.history);
  }

  // save change when user type in their fields
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  render() {
    const { errors } = this.state;
    return (
      <div>
        <NavBar/>
        <div className="row mb-5 ml-auto mr-auto" id="registerPage">
          <div className="col-md-6 m-auto ">
            <img src="/img/logo.png" className="mx-auto d-block"
              id="appLogo" alt="logo"
              width="200" height="200" />

            <div className="card card-body shadow">
              <h1 className="text-center mb-3">
                <i className="fas fa-user-plus"></i> Register
              </h1>
              {
                errors.userExist &&
                <div className="alert alert-danger alert-dismissible fade show" role="alert">
                  {errors.userExist}
                </div>
              }
              <form onSubmit={this.register}>
                <InputGroup htmlFor="name"
                      label="Name"
                      type="name"
                      id="name"
                      name="name"
                  required={true}
                      onChange={this.onChange}
                      error={errors.name}
                      placeholder="Enter full name" />

                <InputGroup htmlFor="email"
                  label="Email"
                  type="email"
                  id="email"
                  name="email"
                  required={true}
                  onChange={this.onChange}
                  error={errors.email}
                  placeholder="Enter email address" />

                <InputGroup htmlFor="password"
                  label="Password"
                  type="password"
                  id="password1"
                  name="password1"
                  required={true}
                  onChange={this.onChange}
                  error={errors.password1}
                  placeholder="Enter password" />

                <InputGroup htmlFor="password"
                  label="Confirm your password"
                  type="password"
                  id="password2"
                  name="password2"
                  required={true}
                  onChange={this.onChange}
                  error={errors.password2}
                  placeholder="Confirm your password" />

                <button type="submit" id='btn-sign-up' className="btn btn-primary btn-block" >
                  Register
                </button>
              </form>

              <p className="lead mt-4 text-center">Already have an account? <a href="/users/login">Login</a></p>
              <p className="text-center">---- Or ----</p>

              <GoogleSignIn />
              <a href="#" id="facebook-button" className="btn btn-block btn-facebook">
                <i className="fa fa-facebook fa-fw"></i> Sign in with Facebook
              </a>
            </div>
          </div>
        </div>
        <Footer />
      </div>
    )
  }
}

Register.propTypes = {
  createUser: PropTypes.func.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  // users: state.users.users,
  user: state.users.user,
  errors: state.errors,
  auth: state.auth
})

export default connect(mapStateToProps, {createUser})(withRouter(Register));
