import React, { Component } from 'react';
import { connect } from 'react-redux';

class Dashboard extends Component {
  state = {
    name: '',
    email: '',
    imageUrl: ''
  }
  componentDidMount() {
    if (this.props.auth.isAuthenticated) {
      this.setState({
        name: this.props.auth.user.name,
        email: this.props.auth.user.email,
        imageUrl: this.props.auth.user.imageUrl,
      })
    } else {
      this.setState({
        name: '',
        email: '',
        imageUrl: '/img/logo.png'
      })
    }
  }
  render() {
    return (
      <div>
        <div className="container mb-5">
          <h1 className="mt-4">Dashboard</h1>
          <img src={this.state.imageUrl !== '' ? this.state.imageUrl:"/img/logo.png"}
            alt="user-icon" id='profileImg'
            width="100" height="100" className="img-thumbnail rounded-circle"/> <br/><br/>

          <p className="lead">Edit information:</p>

          <form encType="multipart/form-data">
            <input type="file" accept="image/*" name="photo"/>
            <input type="submit" value="upload" className="btn btn-outline-primary btn-sm"/>
          </form> <br/>

          <p className="lead mb-3">Welcome {this.state.name}</p>
          <p className="lead mb-2">Email: {this.state.email}</p><br/>
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(mapStateToProps, null)(Dashboard);
