import React, { Component } from 'react';
import ReactTable from 'react-table';
import 'react-table/react-table.css';

export class TransactionsTable extends Component {
  render() {
    const data = [
      {
        date: '2019/05/19',
        amount: 45.89,
        category: 'Bills',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
      {
        date: '2019/03/20',
        amount: 55.89,
        category: 'Gas',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
      {
        date: '2019/05/20',
        amount: -5.89,
        category: 'Electric bills',
        notes: 'Short notes for description',
        payee: 'Trung T Vo'
      },
      {
        date: '2019/06/20',
        amount: 450.89,
        category: 'Tuition',
        notes: 'Short notes for description',
        payee: 'Trung Thanh Vo'
      },
      {
        date: '2019/01/20',
        amount: -445.89,
        category: 'General',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
      {
        date: '2019/05/27',
        amount: 45.89,
        category: 'Cars payment',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
    ]

    const columns = [{
      Header: 'Date',
      accessor: 'date',
      id: 'date'
    }, {
      Header: 'Amount',
      accessor: 'amount',
      Cell: props => <span>{props.value >= 0 ? '+' + props.value : '-' + Math.abs(props.value)}</span>
    }, {
      Header: 'Category',
      accessor: 'category'
    }, {
      Header: 'Notes',
      accessor: 'notes'
    }, {
      Header: 'Payee',
      accessor: 'payee'
    }]

    return (
      <ReactTable
        sorted={[{ 
          id: 'date',
          desc: true
        }]}
        data={data}
        columns={columns}
        defaultPageSize={10}
        pageSizeOptions={[5, 10, 15, 20]}
        defaultSorted={['date']}
      />
    )
  }
}

export default TransactionsTable;
