import { GET_ALL_ACCOUNTS} from "../actions/types";

const initialState = {
  accounts: [],
  account: {},
}

export default function (state = initialState, action) {
  if (action.type === GET_ALL_ACCOUNTS) {
    return {
      ...state,
      accounts: action.payload
    }
  } else {
    return state;
  }
}
