import React, { Component } from 'react';
import InputGroup from '../common/InputGroup';
import {resetPassword} from '../actions/usersActions';
import { connect } from 'react-redux';

export class Forgot extends Component {
  state = {
    email: '',
    message: '',
    success: null
  }

  onSubmit = (e) => {
    e.preventDefault();
    this.props.resetPassword(this.state.email);
  }

  onChange = (e) => {
    this.setState({
      email: e.target.value
    })
  }

  componentWillReceiveProps(nextProps) {
    const { errors, success_msg} = nextProps;
    if (Object.keys(errors).length !== 0){
      this.setState({
        success: false,
        message: errors.err_message
      })
    } else if (Object.keys(success_msg).length !== 0) {
      this.setState({
        success: true,
        message: success_msg.success_msg
      })
    }
  }

  render() {
    return (
      <div className="row mb-5 ml-auto mr-auto">
        <div className="col-md-6 m-auto">
          <img src="/img/logo.png" className="mx-auto d-block"
            id="appLogo" alt="logo" width="200" height="200" />

          <div className="card card-body shadow">
            <h1 className="text-center mb-3">Reset your password</h1>
            {
              this.state.success === true ? 
                <div className="alert alert-success alert-dismissible fade show"
                  role="alert">{this.state.message}
                </div> : null
            }
            <form onSubmit={this.onSubmit}>
              <InputGroup htmlFor="email"
                label="Please enter your email address"
                type="email"
                id="email"
                name="email"
                onChange={this.onChange}
                error={this.state.success ? null : this.state.message}
                placeholder="Enter email address" />

              <p className="lead"><a href='/users/login'>Back to log in</a></p>
              <button type="submit" id="btn-sign-in" className="btn btn-primary btn-block">Send</button>
            </form>
          </div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  errors: state.errors,
  success_msg: state.users.success_msg
})

export default connect(mapStateToProps, {resetPassword})(Forgot);
