import React, { Component } from 'react';
import {Table} from 'react-bootstrap';
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';

class DataTable extends Component {
  state = {
    columns: ['Date', 'Amount', 'Category', 'Balance', 'Notes', 'Payee'],
    rows: [
      {
        date: 'April 15, 2019',
        amount: '$450',
        category: 'Electric bills',
        balance: '$5,000',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
      {
        date: 'Jan 15, 2019',
        amount: '$150',
        category: 'Gas bills',
        balance: '$5,000',
        notes: 'Short notes for description',
        payee: 'Tri Vo'
      },
      {
        date: 'Feb 15, 2019',
        amount: '$40',
        category: 'Water bills',
        balance: '$5,000',
        notes: 'Short notes for description',
        payee: 'Quang Vo'
      },
      {
        date: 'Aug 15, 2019',
        amount: '$450',
        category: 'Groceries',
        balance: '$5,000',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      }
    ]
  }
  render() {
    const {rows} = this.state;
    return (
      <BootstrapTable data={rows} version='4' striped hover>
        <TableHeaderColumn isKey={true} dataField='date'>Date</TableHeaderColumn>
        <TableHeaderColumn dataField='amount'>Amount</TableHeaderColumn>
        <TableHeaderColumn dataField='category'>Category</TableHeaderColumn>
        <TableHeaderColumn dataField='balance'>Balance</TableHeaderColumn>
        <TableHeaderColumn dataField='notes'>Notes</TableHeaderColumn>
        <TableHeaderColumn dataField='payee'>Payee</TableHeaderColumn>
      </BootstrapTable>
    )
  }
}

export default DataTable;
