import React, { Component } from 'react';
import { connect } from 'react-redux';
import NavBar from './NavBar';
import Footer from './Footer';
import ModalDelete from '../common/ModalDelete';

class Dashboard extends Component {
  onChange = (e) => {

  }

  saveInfo = () => {

  }

  deleteAccount = () => {

  }

  render() {
    return (
      <div>
        <NavBar active="dashboard"/>
        <div className="container mt-4 mb-5">

          {
            this.props.auth.isAuthenticated ? 
            // authenticated
            <div className="row justify-content-around">
              <div className="col-md-7" >
                  <h1>Dashboard</h1>
                  <img src={this.props.auth.user.imageUrl !== '' ? this.props.auth.user.imageUrl : "/img/logo.png"}
                    alt="user-icon" id='profileImg'
                    width="100" height="100" className="img-thumbnail rounded-circle" /> <br /><br />

                  <h1>Welcome, {this.props.auth.user.name}</h1><br/>
                  <p className="lead text-dark font-weight-bold">
                    <i className="fas fa-user-edit"></i> Edit information:
                  </p>
                  <p className="lead text-dark">Edit profile image:</p>

                  <form encType="multipart/form-data">
                    <div className="form-group row" style={{ 'display': 'flex', 'alignItems': 'center' }} >
                      <input className="col-7" type="file" accept="image/*" name="photo" />
                      <input className="col-5" type="submit" value="upload" className="btn btn-outline-primary btn-sm" />
                    </div>
                  </form> <br />

                  <form >
                    <div className="form-group row" style={{ 'display': 'flex', 'alignItems': 'center' }}>
                      <label htmlFor="editName" className="col-md-4 col-form-label text-dark">Name:</label>
                      <div className="col-md-8 mt-2">
                        <input type="editName" className="form-control" 
                              id="editName" placeholder="Enter new name"
                              onChange={this.onChange}
                              value={this.props.auth.user.name} />
                      </div>
                    </div>
                  </form>

                  <div className="form-group row" style={{ 'display': 'flex', 'alignItems': 'center' }}>
                    <label htmlFor="email" className="col-md-4 col-form-label text-dark">Email address:</label>
                    <p className="col-md-8 mt-2 text-dark">{this.props.auth.user.email}</p>
                  </div>

                  <p className="lead"><a href='/users/forgot'>Reset password</a></p>
                  <div className="btn-group">
                    <button onSubmit={this.saveInfo} type="submit" className="btn btn-primary mr-2">Save</button>
                    <ModalDelete/>           
                  </div>       

              </div>
            </div> :
            // not authenticated
            <div>
              <h2>Please sign in to view this!</h2>
              <a href="/users/login">Back to login</a>
            </div>
          }

        </div>
        <Footer />
      </div>
    )
  }
}

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(mapStateToProps, null)(Dashboard);
