import React from 'react';
import { MDBDataTable } from 'mdbreact';
import ButtonTooltip from '../common/ButtonTooltip';
import {Form} from 'react-bootstrap';

const edit_btn = <ButtonTooltip
  tooltip_text='Edit'
  btn_text=''
  btn_icon={<i className='fas fa-pen'></i>}
  placement='left'
  type='submit'
  btn_style='btn-secondary btn-sm mr-2' />

const delete_btn = <ButtonTooltip
    tooltip_text='Delete'
    btn_text=''
    btn_icon={<i className='fas fa-trash'></i>}
    placement='left'
    type='submit'
    btn_style='btn-warning btn-sm' />


class DataTablePage extends React.Component {
  state = {
    data: {
      columns: [
        {
          label: 'Date',
          field: 'date',
          sort: 'asc',
          width: 150
        },
        {
          label: 'Amount',
          field: 'amount',
          sort: 'desc',
          width: 120
        },
        {
          label: 'Category',
          field: 'category',
          sort: 'asc',
          width: 150
        },
        {
          label: 'Balance',
          field: 'balance',
          width: 150
        },
        {
          label: 'Notes',
          field: 'notes',
          width: 350
        },
        {
          label: 'Payee',
          field: 'payee',
          sort: 'asc',
          width: 200
        },
        {
          label: 'Edit/Delete',
          field: 'edit',
          width: 150
        }
      ],
      rows: [
        {
          date: 'April 14, 2019',
          amount: '$450',
          category: 'Bills',
          balance: '$500',
          notes: 'Short notes for descriptionShort notes for descriptionShort notes for descriptionShort notes for description',
          payee: 'Trung Vo',
          edit: <div className='btn-group'>
            <Form onSubmit={this.edit_transaction}>
              {edit_btn}
            </Form>
            <Form onSubmit={this.delete_transaction}>
              {delete_btn}
            </Form>
          </div>
        },
        {
          date: 'Jan 15, 2019',
          amount: '$550',
          category: 'Groceries',
          balance: '$1,500',
          notes: 'Short notes for description',
          payee: 'Trung Vo',
          edit: <div className='btn-group'>
            <Form onSubmit={this.edit_transaction}>
              {edit_btn}
            </Form>
            <Form onSubmit={this.delete_transaction}>
              {delete_btn}
            </Form>
          </div>
        },
        {
          date: 'Aug 16, 2019',
          amount: '$250',
          category: 'Cars',
          balance: '$500',
          notes: 'Short notes for description',
          payee: 'Trung Vo',
          edit: <div className='btn-group'>
            <Form onSubmit={this.edit_transaction}>
              {edit_btn}
            </Form>
            <Form onSubmit={this.delete_transaction}>
              {delete_btn}
            </Form>
          </div>
        },
        {
          date: 'Feb 14, 2019',
          amount: '$550',
          category: 'Tuition',
          balance: '$500',
          notes: 'Short notes for description',
          payee: 'Trung Vo',
          edit: <div className='btn-group'>
            <Form onSubmit={this.edit_transaction}>
              {edit_btn}
            </Form>
            <Form onSubmit={this.delete_transaction}>
              {delete_btn}
            </Form>
          </div>
        },
        {
          date: 'May 14, 2019',
          amount: '$150',
          category: 'Drinks',
          balance: '$500',
          notes: 'Short notes for description',
          payee: 'Trung Vo',
          edit: <div className='btn-group'>
            <Form onSubmit={this.edit_transaction}>
              {edit_btn}
            </Form>
            <Form onSubmit={this.delete_transaction}>
              {delete_btn}
            </Form>
          </div>
        },
      ]
    }
  }

  edit_transaction = (e) => {
    e.preventDefault();
    console.log(e)
  }

  delete_transaction = (e) => {
    e.preventDefault();
    console.log(e)
  }

  render() {
    const {data} = this.state;
    return (
      <MDBDataTable
        onClick={(e) => console.log(e)}
        scrollX
        data={data}
      />
    )
  }
}


export default DataTablePage;
