import React, {Component} from 'react';
import { MDBTable, MDBTableBody, MDBTableHead } from 'mdbreact';
import ButtonTooltip from '../common/ButtonTooltip';
import { Form } from 'react-bootstrap';
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';

const edit_btn = <ButtonTooltip
  tooltip_text='Edit'
  btn_text=''
  btn_icon={<i className='fas fa-pen'></i>}
  placement='left'
  type='submit'
  btn_style='btn-secondary btn-sm mr-2' />

const delete_btn = <ButtonTooltip
  tooltip_text='Delete'
  btn_text=''
  btn_icon={<i className='fas fa-trash'></i>}
  placement='left'
  type='submit'
  btn_style='btn-warning btn-sm' />


const selectRowProp = {
  mode: 'checkbox',
  clickToSelect: true,
  bgColor: 'pink'
};

class DataTable extends Component {
  state = {
    columns: ['Date', 'Amount', 'Category', 'Balance', 'Notes', 'Payee', 'Edit/Delete'],
    rows: [
      {
        date: 'April 14, 2019',
        amount: '$450',
        category: 'Bills',
        balance: '$500',
        notes: 'Short notes for descriptionShort notes for descriptionShort notes for descriptionShort notes for description',
        payee: 'Trung Vo',
        edit: <div className='btn-group'>
          <Form onSubmit={this.edit_transaction}>
            {edit_btn}
          </Form>
          <Form onSubmit={this.delete_transaction}>
            {delete_btn}
          </Form>
        </div>
      },
      {
        date: 'Jan 15, 2019',
        amount: '$550',
        category: 'Groceries',
        balance: '$1,500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit: <div className='btn-group'>
          <Form onSubmit={this.edit_transaction}>
            {edit_btn}
          </Form>
          <Form onSubmit={this.delete_transaction}>
            {delete_btn}
          </Form>
        </div>
      },
      {
        date: 'Aug 16, 2019',
        amount: '$250',
        category: 'Cars',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit: <div className='btn-group'>
          <Form onSubmit={this.edit_transaction}>
            {edit_btn}
          </Form>
          <Form onSubmit={this.delete_transaction}>
            {delete_btn}
          </Form>
        </div>
      },
      {
        date: 'Feb 14, 2019',
        amount: '$550',
        category: 'Tuition',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit: <div className='btn-group'>
          <Form onSubmit={this.edit_transaction}>
            {edit_btn}
          </Form>
          <Form onSubmit={this.delete_transaction}>
            {delete_btn}
          </Form>
        </div>
      },
      {
        date: 'May 14, 2019',
        amount: '$150',
        category: 'Drinks',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo',
        edit: <div className='btn-group'>
          <Form onSubmit={this.edit_transaction}>
            {edit_btn}
          </Form>
          <Form onSubmit={this.delete_transaction}>
            {delete_btn}
          </Form>
        </div>
      },
    ]
  }

  render () {
    const { rows} = this.state;
    return (
      <BootstrapTable data={rows} selectRow={selectRowProp} version='4' >
        <TableHeaderColumn dataField='date' isKey>Date</TableHeaderColumn>
        <TableHeaderColumn dataField='amount'>Amount</TableHeaderColumn>
        <TableHeaderColumn dataField='category'>Category</TableHeaderColumn>
        <TableHeaderColumn dataField='balance'>Balance</TableHeaderColumn>
        <TableHeaderColumn dataField='notes'>Notes</TableHeaderColumn>
        <TableHeaderColumn dataField='payee'>Payee</TableHeaderColumn>
        <TableHeaderColumn dataField='edit'>Edit/Delete</TableHeaderColumn>
      </BootstrapTable>
    );
  }
}

export default DataTable;
