import React, { Component } from 'react';
import { connect } from 'react-redux';
import {resetPassword} from '../actions/usersActions';
import InputGroup from '../common/InputGroup';

export class Reset extends Component {
  state = {
    password: '',
    confirm_password: '',
    success: null,
    message: ''
  }
  componentDidMount() {
    this.props.resetPassword(this.props.match.params.token, this.props.history);
  }

  componentWillReceiveProps(nextProps) {
    console.log(nextProps);
  }

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    }, () => {
      console.log('Pass: ' + this.state.password)
      console.log('Confirm_password: ' + this.state.confirm_password)
    })
  }

  render() {
    return (
      <div className="row mb-5 ml-auto mr-auto">
        <div className="col-md-6 m-auto">
          <img src="/img/logo.png" className="mx-auto d-block"
            id="appLogo" alt="logo" width="200" height="200" />

          <div className="card card-body shadow">
            <h1 className="text-center mb-3">
              <i className="fas fa-lock">&nbsp;</i> Reset your password
            </h1>

            <form action="/users/reset/<%= typeof user != 'undefined'? user.resetPasswordToken : token %>"
                method="POST">

              <InputGroup htmlFor="password"
                label="New password"
                type="password"
                id="password"
                name="password"
                onChange={this.onChange}
                error={
                  this.state.success ? false : this.state.message
                }
                placeholder="Enter new password" />

              <InputGroup htmlFor="password"
                label="Confirm new password"
                type="password"
                id="confirm_password"
                name="confirm_password"
                onChange={this.onChange}
                error={
                  this.state.success ? false : this.state.message
                }
                placeholder="Confirm new password" />

              <button type="submit" id="btn-sign-in" className="btn btn-primary btn-block">Update</button>
            </form>
          </div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  errors: state.errors,
  success: state.success,
  user: state.users.user
})

export default connect(mapStateToProps, {resetPassword})(Reset);
