import React, { Component } from 'react';
import InputGroup from '../common/InputGroup';

export class Contact extends Component {
  state = {
    name: '',
    email: '',
    phone: '',
    message: ''
  }
  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }
  render() {
    return (
      <div className="row mb-5 ml-auto mr-auto">
        <div className="col-md-6 m-auto">
          <img src="/img/logo.png" className="mx-auto d-block"
            id="appLogo" alt="logo" width="200" height="200" />

          <div className="card card-body shadow">
            <h1 className="text-center mb-3"><i class="fas fa-paper-plane"></i> Contact</h1>

            <form onSubmit={this.onSubmit}>
              <InputGroup htmlFor="name"
                label="Name"
                type="name"
                id="name"
                name="name"
                onChange={this.onChange}
                error={null}
                placeholder="Enter name" />

              <InputGroup htmlFor="email"
                label="Email"
                type="email"
                id="email"
                name="email"
                onChange={this.onChange}
                error={null}
                placeholder="Enter email address" />

              <InputGroup htmlFor="phone"
                label="Phone number"
                type="phone"
                id="phone"
                name="phone"
                onChange={this.onChange}
                error={null}
                placeholder="Enter phone number" />

              <div className="form-group">
                <label htmlFor="message">Message</label>
                <textarea className="form-control" id="message" rows="6" placeholder='Enter your message'></textarea>
              </div>

              <button type="submit" id="btn-sign-in" className="btn btn-primary btn-block">Send</button>
            </form>
          </div>
        </div>
      </div>
    )
  }
}

export default Contact;
