import React, { Component } from 'react';
import { connect } from 'react-redux';

export class ModalDelete extends Component {
  onSubmit = (e) => {
    e.preventDefault();
    console.log('Clicked');
    console.log(this.props.auth.user);
  }
  render() {
    const {header, body, buttonText, cancelBtn, yesBtn} = this.props;
    return (
      <div>
        <button onSubmit={this.deleteAccount} data-toggle="modal"
                data-target="#exampleModal"
                type="button" className="btn btn-danger">{buttonText}
        </button>

        <div className="modal fade" id="exampleModal" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">{header}</h5>
                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">
                {body}
              </div>
              <form className="modal-footer" onSubmit={this.onSubmit} >
                <button type="submit" data-dismiss="modal"
                   className="btn btn-outline-secondary">{yesBtn}</button>
                <button type="button" className="btn btn-primary" data-dismiss="modal">{cancelBtn}</button>
              </form>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(mapStateToProps, null)(ModalDelete);
