import * as React from 'react';
import {currencyTypes} from '../dictionary/dictionary';
import {
  PagingState,
  IntegratedPaging,
  DataTypeProvider,
  SearchState,
  IntegratedFiltering,
  EditingState,
  SortingState,
  IntegratedSorting
} from '@devexpress/dx-react-grid';

import {
  Grid, Table,
  TableHeaderRow, PagingPanel,
  SearchPanel, Toolbar,
  TableEditRow,
  TableEditColumn,
  TableFixedColumns
} from '@devexpress/dx-react-grid-bootstrap4';
import {connect} from 'react-redux';


//import "@devexpress/dx-react-grid-bootstrap4/dist/dx-react-grid-bootstrap4.css";

/*
==================================================================
Currency formatter
==================================================================
*/
// convert number to comma form with thousands separator
function numberWithCommas(x) {
  return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
}

const PaymentFormatter = (props) => {
  const {value, row} = props;
  const {currency} = row;
  return (
    <b style={{ color: value >= 0 ? 'green' : 'red' }}>
      {value >= 0 ? '+' : '-'}<small>{currency === 'USD' ? currencyTypes.USD : (currency === 'VND' ? currencyTypes.VND : currencyTypes.EUR)}</small>{numberWithCommas(Math.abs(value))}
    </b>
  );
}

const PaymentTypeProvider = props => {
  return (
    <DataTypeProvider
      formatterComponent={PaymentFormatter}
      {...props}
    />
  );
}

/*
==================================================================
Dates formatter
==================================================================
*/
const DateFormatter = (props) => {
  const { row } = props;
  const { date } = row;

  return (
    <span >
      {date}
    </span>
  )
}

const DateTypeProvider = props => (
  <DataTypeProvider
    formatterComponent={DateFormatter}
    {...props}
  />
);

/*
==================================================================
Notes formatter
==================================================================
*/
const NotesFormatter = (props) => {
  const { row } = props;
  const { notes } = row;

  return (
    <small>
      {notes}
    </small>
  );
}

const NotesTypeProvider = props => (
  <DataTypeProvider
    formatterComponent={NotesFormatter}
    {...props}
  />
);

/*
==================================================================
Table styles
==================================================================
*/
const TableComponent = ({ ...restProps }) => {
  return (
    <Table.Table
      {...restProps}
      className="table-striped"
    />
  )
}

const getRowId = row => row.id;

const toolComponent = ({children}) => {
  return (
    <div className='font-weight-bold bg-primary text-white p-3' style={{display: 'flex', alignItems: 'center' }}>      
      Current balance: $1,000{children}
    </div>
  )
}

/**
 * ==================================================================
 * Command button styles
 * ==================================================================
 */
const CommandButton = ({
  onExecute, icon, text, hint, color,
}) => {
  return (
    <button
      type="button"
      className="btn btn-link btn-sm mt-auto mb-auto"
      style={{padding: 11}}
      onClick={(e) => {
        onExecute();
        e.stopPropagation();
      }}
      title={hint}
    >
      <span className={color || 'undefined'}>
        {icon ? <i className={`fas fa-${icon}`} /> : null}
        <b>{text}</b>
      </span>
    </button>
  );
}

const AddButton = ({ onExecute }) => (
  <CommandButton icon="plus" hint="Create new row" onExecute={onExecute} text={'Add'} />
);

const EditButton = ({ onExecute }) => (
  <CommandButton icon="pen" hint="Edit row" color="text-warning" onExecute={onExecute} />
);

const DeleteButton = ({ onExecute }) => (
  <CommandButton
    icon="trash"
    hint="Delete row"
    color="text-danger"
    onExecute={() => {
      // eslint-disable-next-line
      if (window.confirm('Are you sure you want to delete this row?')) {
        onExecute();
      }
    }}
  />
);

const CommitButton = ({ onExecute }) => (
  <CommandButton icon="check" hint="Save changes" color="text-success" onExecute={onExecute} />
);

const CancelButton = ({ onExecute }) => (
  <CommandButton icon="times" hint="Cancel changes" color="text-danger" onExecute={onExecute} />
);

const commandComponents = {
  add: AddButton,
  edit: EditButton,
  delete: DeleteButton,
  commit: CommitButton,
  cancel: CancelButton,
};

const Command = ({ id, onExecute }) => {
  const ButtonComponent = commandComponents[id];
  return (
    <ButtonComponent
      onExecute={onExecute}
    />
  );
};

const SortingIcon = ({ direction }) => (
  <span
    className={`fas fa-arrow-${direction === 'asc' ? 'up' : 'down'}`}
  />
);

const SortLabel = ({ onSort, children, direction }) => {
  return (
    <a
      className="font-weight-bold"
      onClick={onSort}
      style={{cursor: 'pointer' }}
    >
      {children}
      {(direction && <SortingIcon direction={direction} />)}
      {children.props.children === 'Date' ? <div><small>(yyyy/mm/dd)</small></div> : null}
    </a>
  );
}

