const LookupEditCell = ({
  column, selectColumns_values, value, onValueChange,
}) => (
    <td
      style={{
        verticalAlign: 'middle',
        padding: 1,
      }}
    >
      <select
        className="form-control"
        style={{ width: '100%', textAlign: column.align }}
        value={value}
        onChange={e => onValueChange(e.target.value)}
      >
        {selectColumns_values.map(val => (
          <option key={val} value={val}>
            {val}
          </option>
        ))}
      </select>
    </td>
  );

const Cell = (props) => {
  return <Table.Cell {...props} style={{ verticalAlign: 'middle' }} />;
};

const EditColumn = (props) => {
  return <Table.Cell {...props} style={{ verticalAlign: 'middle' }} className='text-center' />;
}

const selectColumns = {
  currency: Object.keys(currencyTypes)
};

const EditCell = (props) => {
  const { column } = props;
  const selectColumns_values = selectColumns[column.name];
  if (selectColumns_values) {
    return <LookupEditCell {...props} selectColumns_values={selectColumns_values} style={{ verticalAlign: 'middle' }} />;
  }
  return <TableEditRow.Cell {...props} style={{ verticalAlign: 'middle' }} />;
};

const HeaderStyles = (props) => {
  return <TableHeaderRow.Cell {...props} style={{ verticalAlign: 'middle' }} />
}


class DataTable extends React.PureComponent {
  constructor(props) {
    super(props);
    const {data} = this.props;
    this.state = {
      //fixedColumn: window.innerWidth > 945,
      columns: [
        { name: 'date', title: `Date` },
        { name: 'amount', title: 'Amount' },
        { name: 'category', title: 'Category' },
        { name: 'currency', title: 'Currency' },
        { name: 'notes', title: 'Notes' },
        { name: 'payee', title: 'Payee' }
      ],
      tableColumnExtensions: [
        { columnName: 'date', align: 'center', width: 110 },
        { columnName: 'amount', align: 'right', width: 110 },
        { columnName: 'currency', align: 'right', width: 70 },
        { columnName: 'category', align: 'right', width: 130 },
        { columnName: 'notes', align: 'center', wordWrapEnabled: true, width: 350 },
        { columnName: 'payee', align: 'left', width: 110 },
      ],
      rows: data
        // {
        //   id: 1,
        //   date: '2019/09/14',
        //   amount: 450,
        //   category: 'Bills for electric',
        //   currency: 'VND',
        //   notes: '',
        //   payee: 'Trung Vo',
        // }
      ,
      sorting: [{columnName: 'date', direction: 'desc'}],
      paymentColumns: ['amount'],
      currentPage: 0,
      pageSize: 10,
      pageSizes: [10, 20, 30],
      searchValue: '',
      //leftFixedColumns: [TableEditColumn.COLUMN_TYPE],
      editingRowIds: [],
      addedRows: [],
      rowChanges: {},
    };
    this.changeCurrentPage = currentPage => this.setState({ currentPage });
    this.changePageSize = pageSize => this.setState({ pageSize });
    this.changeSearchValue = value => this.setState({ searchValue: value });
    this.changeSorting = sorting => this.setState({ sorting });
  }

  componentDidUpdate() {
    const { data } = this.props;
    this.setState({
      rows: data
    })
  }

  // componentDidMount() {
  //   this._isMounted = true;
  //   window.addEventListener('resize', () => {
  //     if (window.innerWidth <= 945) {
  //       if (this._isMounted) {
  //         this.setState({ fixedColumn: false })
  //       }
  //     } else {
  //       if (this._isMounted) {
  //         this.setState({ fixedColumn: true })
  //       }
  //     }
  //   });
  // }


  // update rows after adding
  changeAddedRows = (addedRows) => {
    console.log(addedRows);
    const d = new Date();
    this.setState({
      addedRows:
        addedRows.map(row => (Object.keys(row).length ? row : {
          date: d.getFullYear() + '/' + String(d.getMonth() + 1).padStart(2,'0') + '/' + String(d.getDate()).padStart(2,'0'),
          //date: new Date().toISOString(),
          amount: 0.0,
          category: 'General',
          currency: Object.keys(currencyTypes)[0],
          payee: this.props.auth.isAuthenticated ? this.props.auth.user.name : ''
        }))
    })
  }

  // update rows after editing
  changeEditingRowIds = (editingRowIds) => {
    console.log(editingRowIds);
    this.setState({ editingRowIds });
  }

  // update rows after changing
  changeRowChanges = (rowChanges) => {
    console.log(rowChanges);
    this.setState({ rowChanges });
  }

  // updates rows after modifying
  commitChanges = ({ added, changed, deleted }) => {
    console.log(added);

    let { rows } = this.state;
    if (added) {
      const startingAddedId = rows.length > 0 ? rows[rows.length - 1].id + 1 : 0;
      rows = [
        ...rows,
        ...added.map((row, index) => ({
          id: startingAddedId + index,
          ...row,
        })),
      ];
    }
    if (changed) {
      rows = rows.map(row => (changed[row.id] ? { ...row, ...changed[row.id] } : row));
    }
    if (deleted) {
      rows = this.deleteRows(deleted);
    }
    this.setState({ rows });
  }

  // update rows after deleting
  deleteRows = (deletedIds) => {
    const rows = this.state.rows.slice();
    deletedIds.forEach((rowId) => {
      const index = rows.findIndex(row => row.id === rowId);
      if (index > -1) {
        rows.splice(index, 1);
      }
    });
    return rows;
  };


  render() {
    const { 
      rows, columns, tableColumnExtensions, searchValue, sorting,
      paymentColumns, pageSize, pageSizes, currentPage,
      editingRowIds, rowChanges, addedRows,
      //fixedColumn, leftFixedColumns
    } = this.state;

    return (
      <div className="card">
        <Grid
          rows={rows}
          columns={columns}
          getRowId={getRowId}
        >
          {/* Paging for table */}
          <PagingState
            defaultCurrentPage={currentPage}
            onCurrentPageChange={this.changeCurrentPage}
            pageSize={pageSize}
            onPageSizeChange={this.changePageSize}
          />
          <IntegratedPaging />
          <PagingPanel pageSizes={pageSizes} />

          {/* Currency styles formatter */}
          <PaymentTypeProvider for={paymentColumns} />
          <DateTypeProvider for={['date']} />
          <NotesTypeProvider for={['notes']} />

          {/* Searching tool */}
          <SearchState
            value={searchValue}
            onValueChange={this.changeSearchValue}
          />
          <IntegratedFiltering />
          <Toolbar rootComponent={toolComponent} />
          <SearchPanel />

          {/** Sorting tools */}
          <SortingState
            sorting={sorting}
            defaultSorting={sorting}
            onSortingChange={this.changeSorting}
          />
          <IntegratedSorting />

          {/* Editing tool */}
          <EditingState
            editingRowIds={editingRowIds}
            onEditingRowIdsChange={this.changeEditingRowIds}
            rowChanges={rowChanges}
            onRowChangesChange={this.changeRowChanges}
            addedRows={addedRows}
            onAddedRowsChange={this.changeAddedRows}
            onCommitChanges={this.commitChanges}
          />

          {/* Table itself */}
          <Table columnExtensions={tableColumnExtensions} 
                tableComponent={TableComponent}
                cellComponent={Cell}
          />

          <TableHeaderRow showSortingControls 
                  sortLabelComponent={SortLabel} 
                  cellComponent={HeaderStyles} 
          />
          <TableEditRow cellComponent={EditCell} />
          <TableEditColumn
            cellComponent={EditColumn}
            showAddCommand={!addedRows.length}
            showEditCommand
            showDeleteCommand
            commandComponent={Command}
          />
          {/* <TableFixedColumns
            leftColumns={ fixedColumn ? leftFixedColumns : [] }
          /> */}


        </Grid>
      </div>
    );
  }
}

