import React, { Component } from 'react';
import InputGroup from '../common/InputGroup';
import {addAccount} from '../actions/accountActions';
import {connect} from 'react-redux';
import { Alert } from 'react-bootstrap';

export class AddAccount extends Component {
  state = {
    name: '',
    currency: '',
    balance: '',
    budget: '',
    errors: {},
    message: '',
    show: true
  }

  addAccount = (e) => {
    e.preventDefault();
    const newAccount = {
      name: this.state.name,
      currency: this.state.currency,
      balance: this.state.balance,
      budget: this.state.budget
    }
    this.props.addAccount(newAccount);
  }

  onChange = (e) => {
    this.setState({
      [e.target.id]: e.target.value
    })
  }

  componentWillReceiveProps(nextProps) {
    const {errors, success} = nextProps;
    if (typeof errors === 'object' && Object.keys(errors).length > 0) {
      this.setState({ errors })
    } else {
      this.setState({ errors: {} })
    }
    if (typeof success.add !== 'undefined' && success.add !== '') {
      this.setState({
        message: success.add
      })
    } else {
      this.setState({
        message: ''
      })
    }
  }

  render() {
    const {errors, message} = this.state;
    return (
      <div className="container">
        <div className='card card-header'>
          <h2>
            <i className='fas fa-plus-circle'>&nbsp;&nbsp;</i>Add account
          </h2>
        </div>
        <div className='card card-body'>
          {
            message.length > 0 ?
              <Alert dismissible variant="success" show={this.state.show} onClose={() => this.setState({show: false})}>
                {message}
              </Alert> : null
          }

          <InputGroup htmlFor="name"
            label="Name"
            type="name"
            id="name"
            name="name"
            required={true}
            error={errors.name}
            onChange={this.onChange}
            placeholder="Enter account name" />


          <div className="form-group row justify-content-start" style={{ 'display': 'flex', 'alignItems': 'center' }}>
            <label htmlFor="currency" className="col-md-4 col-sm-6 col-xs-12 col-form-label">
              Select currency: <span style={{ color: 'red' }}>*</span>
            </label>
            <div className="form-group col-md-7 col-sm-5 col-xs-12 mt-2">
              <select className="custom-select" id='currency' 
                      onChange={this.onChange}>
                <option value="">Select currency</option>
                <option value="USD">USD</option>
                <option value="VND">VND</option>
                <option value="EUR">EUR</option>
              </select>
              {errors.currency && <small className="form-text" style={{ color: 'red' }}>Test err</small> }
            </div>
          </div>

          <InputGroup htmlFor="balance"
            label="Balance"
            type="balance"
            id="balance"
            name="balance"
            required={true}
            error={errors.balance}
            onChange={this.onChange}
            placeholder="Enter account balance" />

          <InputGroup htmlFor="budget"
            label="Budget"
            type="budget"
            id="budget"
            name="budget"
            required={false}
            onChange={this.onChange}
            placeholder="Enter budget" />

          <form onSubmit={this.addAccount}>
            <button type='submit' className='btn btn-secondary'>Add</button>
          </form>
        </div>

      </div>
    )
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors,
  success: state.success
})

export default connect(mapStateToProps, {addAccount})(AddAccount);
