import * as React from 'react';
import {
  PagingState,
  IntegratedPaging,
  DataTypeProvider
} from '@devexpress/dx-react-grid';

import {
  Grid, Table,
  TableHeaderRow, PagingPanel
} from '@devexpress/dx-react-grid-bootstrap4';

//import "@devexpress/dx-react-grid-bootstrap4/dist/dx-react-grid-bootstrap4.css";

const CurrencyFormatter = ({ value }) => (
  <b style={{ color: value >= 0 ? 'green' : 'red' }}>
    {value >= 0 ? '+':'-'}${Math.abs(value)}
  </b>
);

const CurrencyTypeProvider = props => (
  <DataTypeProvider
    formatterComponent={CurrencyFormatter}
    {...props}
  />
);

const TableComponent = ({ ...restProps }) => (
  <Table.Table
    {...restProps}
    className="table-striped"
  />
);


class DataTable extends React.PureComponent {
  constructor(props) {
    super(props);

    this.state = {
      columns: [
        { name: 'date', title: 'Date' },
        { name: 'amount', title: 'Amount' },
        { name: 'category', title: 'Category' },
        { name: 'balance', title: 'Balance' },
        { name: 'notes', title: 'Notes' },
        { name: 'payee', title: 'Payee' }
      ],
      tableColumnExtensions: [
        { columnName: 'date', align: 'center', width: 90 },
        { columnName: 'amount', align: 'right', width: 110 },
        { columnName: 'balance', align: 'right', width: 110 },
        { columnName: 'category', align: 'center', width: 130 },
        { columnName: 'notes', align: 'center', wordWrapEnabled: true, width: 350 },
        { columnName: 'payee', align: 'center', width: 180 }
      ],
      rows: [
        {
          date: 'April 14, 2019',
          amount: 450,
          category: 'Bills',
          balance: 500,
          notes: '',
          payee: 'Trung Vo'
        },
        {
          date: 'Jan 15, 2019',
          amount: -550,
          category: 'Groceries',
          balance: 1500,
          notes: '',
          payee: 'Trung Vo'
        },
        {
          date: 'Aug 16, 2019',
          amount: 250,
          category: 'Cars',
          balance: 500,
          notes: 'Short notes',
          payee: 'Trung Vo'
        },
        {
          date: 'Feb 14, 2019',
          amount: -550,
          category: 'Tuition',
          balance: 500,
          notes: 'Short notes',
          payee: 'Trung Vo'
        },
        {
          date: 'May 14, 2019',
          amount: 150,
          category: 'Drinks',
          balance: 500,
          notes: '',
          payee: 'Trung Vo'
        },
        {
          date: 'May 14, 2019',
          amount: 150,
          category: 'Drinks',
          balance: 500,
          notes: '',
          payee: 'Trung Vo'
        },
        {
          date: 'May 14, 2019',
          amount: -150,
          category: 'Drinks',
          balance: 500,
          notes: '',
          payee: 'Trung Vo'
        },
        {
          date: 'May 14, 2019',
          amount: 150,
          category: 'Drinks',
          balance: 500,
          notes: '',
          payee: 'Trung Vo'
        },
        {
          date: 'May 14, 2019',
          amount: 150,
          category: 'Drinks',
          balance: 500,
          notes: '',
          payee: 'Trung Vo'
        },
        {
          date: 'May 14, 2019',
          amount: 150,
          category: 'Drinks',
          balance: 500,
          notes: '',
          payee: 'Trung Vo'
        }
      ],
      currencyColumns: ['amount', 'balance'],
      currentPage: 0,
      pageSize: 7,
      pageSizes: [7, 14, 28]
    };

    this.changeCurrentPage = currentPage => this.setState({ currentPage });
    this.changePageSize = pageSize => this.setState({ pageSize });
  }

  render() {
    const {
      rows, columns, tableColumnExtensions,
      currencyColumns, pageSize, pageSizes, currentPage
    } = this.state;

    return (
        <Grid rows={rows} columns={columns} >
          <PagingState
            defaultCurrentPage={currentPage}
            onCurrentPageChange={this.changeCurrentPage}
            pageSize={pageSize}
            onPageSizeChange={this.changePageSize}
          />
          <CurrencyTypeProvider
            for={currencyColumns}
          />
          <IntegratedPaging />
          <Table columnExtensions={tableColumnExtensions} tableComponent={TableComponent} />
          <TableHeaderRow />
          <PagingPanel pageSizes={pageSizes} />
        </Grid>
    );

  }
}

export default DataTable;
