import axios from 'axios';
import * as actions from './types';
import jwt_decode from 'jwt-decode';

// register new user
export const createUser = (newUser, history) => dispatch => {
  axios.post('/users/register', newUser)
    .then(res => {
      dispatch({
        type: actions.ADD_USER,
        payload: res.data.newUser
      })
      history.push({
        pathname: '/users/login',
        state: {success: true}
      });
    })
    .catch(err => {
      dispatch({
        type: actions.GET_ERRORS,
        payload: err.response.data
      })
    })
};

// login with email and password
export const loginUser = (user) => dispatch => {
  axios.post('/users/login', user)
    .then(res => {
      // save token to localstorage
      localStorage.setItem('jwtToken', res.data.token);
      // Set token to Auth header
      if (res.data.token) {
        // Apply to every request
        axios.defaults.headers.common['Authorization'] = res.data.token;
      }
      // Decode token to get user data
      const decoded = jwt_decode(res.data.token);
      // Set current user
      dispatch({
        type: actions.SET_CURRENT_USER,
        payload: decoded
      })
    })
    .catch(err => {
      dispatch({
        type: actions.GET_ERRORS,
        payload: err.response.data
      })
    })
}

// Login with Google
export const googleSignIn = (tokenObj) => dispatch => {
  var token = tokenObj.id_token;
  // decoded user info with JWT token
  var decoded = jwt_decode(token);

  // set user
  const user = {
    name: decoded.name,
    email: decoded.email,
    imageUrl: decoded.picture
  }
  // call POST request
  axios.post('/users/googleLogin', user)
    .then(res => {
      // save token to localstorage
      localStorage.setItem('jwtToken', res.data.token);
      // Apply to every request
      axios.defaults.headers.common['Authorization'] = res.data.token;
      // decoded user info with JWT token
      var decoded = jwt_decode(res.data.token);
      // Set current user
      dispatch({
        type: actions.SET_CURRENT_USER,
        payload: decoded
      })
    })
    .catch(err => {
      dispatch({
        type: actions.GET_ERRORS,
        payload: err.response.data
      })
    })
};

// Log user out
export const logoutUser = () => dispatch => {
  // Remove token from localStorage
  localStorage.removeItem('jwtToken');
  // Remove auth header for future requests
  // Delete auth header
  delete axios.defaults.headers.common['Authorization'];
  // Set current user to {} which will set isAuthenticated to false
  dispatch({
    type: actions.SET_CURRENT_USER,
    payload: {}
  })
};

// verify email before reset user password
export const verifyEmail = (email) => dispatch => {
  dispatch(clearErrors());
  dispatch(clearSuccess());

  axios.post('/users/verify', {email: email})
    .then(res => {
      dispatch({
        type: actions.GET_SUCCESS,
        payload: res.data
      })
    })
    .catch(err => {
      dispatch({
        type: actions.GET_ERRORS,
        payload: err.response.data
      })
    });
};

// reset user password
export const resetPassword = (token, history) => dispatch => {
  axios.get(`/users/reset/${token}`)
    .then(res => {
      dispatch({
        type: actions.VERIFY_USER,
        payload: {
          id: res.data['_id'],
          resetPasswordToken: res.data.resetPasswordToken,
          resetPasswordExpires: res.data.resetPasswordExpires
        }
      })
      dispatch(clearSuccess());
    })
    .catch(err => {
      dispatch({
        type: actions.GET_ERRORS,
        payload: err.response.data
      })
      history.push('/users/forgot');
    })
}

// Start reset password by comparing the two, then update!
export const updatePassword = (user, password, confirm_password) => dispatch => {
  axios.post(`/users/reset/update_password`, {
    user: user,
    password: password,
    confirm_password: confirm_password
  })
  .then(res => {
    dispatch({
      type: actions.GET_SUCCESS,
      payload: res.data
    })
  })
  .catch(err => {
    dispatch({
      type: actions.GET_ERRORS,
      payload: err.response.data
    })
  })
}

// delete user account
export const deleteAccount = () => dispatch => {
  if (window.confirm('Are you sure? This can NOT be undone!')) {
    axios.delete('/users/delete')
      .then(res => {
        localStorage.removeItem('jwtToken');
        delete axios.defaults.headers.common['Authorization'];
        dispatch({
          type: actions.SET_CURRENT_USER,
          payload: {}
        })
        dispatch({
          type: actions.GET_SUCCESS,
          payload: res.data
        })
      })
      .catch(err => {
        dispatch({
          type: actions.GET_ERRORS,
          payload: err.response.data
        })
      })
  }
}

// Clear errors
export const clearErrors = () => {
  return {
    type: actions.CLEAR_ERRORS
  };
};

// Clear success
export const clearSuccess = () => {
  return {
    type: actions.CLEAR_SUCCESS
  };
};
