import React, { Component } from 'react';
import InputGroup from '../common/InputGroup';
import NavBar from './NavBar';
import Footer from './Footer';
import {contact} from '../actions/usersActions';
import {connect} from 'react-redux';

export class Contact extends Component {
  state = {
    name: '',
    email: '',
    phone: '',
    message: '',
    notify_message: '',
    errors: {},
    success: null,
  }

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  onSubmit = (e) => {
    e.preventDefault();
    this.props.contact({
      name: this.state.name,
      email: this.state.email,
      phone: this.state.phone,
      message: this.state.message
    })
  }

  componentWillReceiveProps(nextProps) {
    console.log(nextProps);
    if (typeof nextProps.errors !== 'object' && Object.keys(nextProps.errors).length !== 0 ) {
      this.setState({
        errors: nextProps.errors
      })
    } else if (typeof nextProps.success !== 'object' && Object.keys(nextProps.success).length !== 0) {
      this.setState({
        success: true,
        notify_message: nextProps.success.success_msg
      })
    } else {
      this.setState({
        success: null
      })
    }
  }

  render() {
    const {errors, success, notify_message} = this.state;
    return (
      <div>
        <NavBar active="contact" />
        <div className="row mb-5 ml-auto mr-auto" id="contactPage">
          <div className="col-md-6 m-auto">
            <img src="/img/logo.png" className="mx-auto d-block"
              id="appLogo" alt="logo" width="200" height="200" />

            <div className="card card-body shadow">
              <h1 className="text-center mb-3"><i className="fas fa-paper-plane"></i> Contact</h1>
              {
                (success !== null && typeof success !== 'undefined' && success) ? 
                <div className="alert alert-success alert-dismissible fade show"
                    role="alert">{notify_message}</div>
                : null
              }
              <form onSubmit={this.onSubmit}>
                <InputGroup htmlFor="name"
                  label="Name"
                  type="name"
                  id="name"
                  name="name"
                  onChange={this.onChange}
                  error={typeof errors.name !== 'undefined' ? errors.name:false}
                  placeholder="Enter name" />

                <InputGroup htmlFor="email"
                  label="Email"
                  type="email"
                  id="email"
                  name="email"
                  onChange={this.onChange}
                  error={typeof errors.email !== 'undefined' ? errors.email : false}
                  placeholder="Enter email address" />

                <InputGroup htmlFor="phone"
                  label="Phone number"
                  type="phone"
                  id="phone"
                  name="phone"
                  onChange={this.onChange}
                  error={typeof errors.phone !== 'undefined' ? errors.phone : false}
                  placeholder="Enter phone number" />

                <InputGroup htmlFor="message"
                  label="Message"
                  type="message"
                  id="message"
                  name="message"
                  onChange={this.onChange}
                  error={typeof errors.message !== 'undefined' ? errors.message : false}
                  placeholder="Enter your message" />

                <button type="submit" id="btn-sign-in" className="btn btn-primary btn-block">Send</button>
              </form>
            </div>
          </div>
        </div>
        <Footer />
      </div>
    )
  }
}

const mapStateToProps = state => ({
  errors: state.errors,
  success: state.success
})

export default connect(mapStateToProps, {contact})(Contact);
