import * as React from 'react';

import {
  Grid, Table,
  VirtualTable, TableHeaderRow, TableFilterRow, TableSelection, TableGroupRow,
  GroupingPanel, DragDropProvider, TableColumnReordering, Toolbar, SearchPanel,
} from '@devexpress/dx-react-grid-bootstrap4';

//import "@devexpress/dx-react-grid-bootstrap4/dist/dx-react-grid-bootstrap4.css";


class DataTable extends React.PureComponent {
  constructor(props) {
    super(props);

    this.state = {
      columns: [
        { name: 'date', title: 'Date' },
        { name: 'amount', title: 'Amount' },
        { name: 'category', title: 'Category' },
        { name: 'balance', title: 'Balance' },
        { name: 'notes', title: 'Notes' },
        { name: 'payee', title: 'Payee' }
      ],
      tableColumnExtensions: [
        { columnName: 'date', align: 'center' },
        { columnName: 'amount', align: 'right', width: 110 },
        { columnName: 'balance', align: 'right', width: 110 },
        { columnName: 'category', align: 'center', width: 130 },
        { columnName: 'notes', align: 'center', wordWrapEnabled: true, width: 150 },
        { columnName: 'payee', align: 'center', width: 120 }
      ],
      rows: [
        {
          date: 'April 14, 2019',
          amount: '$450',
          category: 'Bills',
          balance: '$500',
          notes: 'Short notes for descriptionShort notes for descriptionShort notes for descriptionShort notes for description',
          payee: 'Trung Vo'
        },
        {
          date: 'Jan 15, 2019',
          amount: '$550',
          category: 'Groceries',
          balance: '$1,500',
          notes: 'Short notes for description',
          payee: 'Trung Vo'
        },
        {
          date: 'Aug 16, 2019',
          amount: '$250',
          category: 'Cars',
          balance: '$500',
          notes: 'Short notes for description',
          payee: 'Trung Vo'
        },
        {
          date: 'Feb 14, 2019',
          amount: '$550',
          category: 'Tuition',
          balance: '$500',
          notes: 'Short notes for description',
          payee: 'Trung Vo'
        },
        {
          date: 'May 14, 2019',
          amount: '$150',
          category: 'Drinks',
          balance: '$500',
          notes: 'Short notes for description',
          payee: 'Trung Vo'
        }
      ],
      currencyColumns: ['amount'],
    };
  }

  render() {
    const {
      rows, columns, tableColumnExtensions,
      currencyColumns
    } = this.state;

    return (
        <Grid rows={rows} columns={columns} >
          <Table columnExtensions={tableColumnExtensions} />
          <TableHeaderRow />
        </Grid>
    );

  }
}

export default DataTable;
