import React, { Component } from 'react';

export class ManageAccount extends Component {
  render() {
    const { accounts} = this.props;
    return (
      <div>
        {
          !accounts.loading ? 
            <p>You currently have {accounts.accounts.length} accounts.</p> : null 
        } 
      </div>
    )
  }
}

export default ManageAccount;
