import React, { Component } from 'react';
import {FacebookLogin} from 'react-facebook-login';
import { facebookSignIn } from '../actions/usersActions';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

class FacebookSignIn extends Component {
  state = {
    appId: '2540434142655804',
  }

  // Success sign in with Google
  onSuccess = (response) => {
    //console.log(response);
    //this.props.googleSignIn(response.tokenObj, this.props.history);
  }


  // fail to sign in
  onFailure = (response) => {

  }

  responseFacebook = (response) => {
    console.log(response);
  }

  onClick = () => {
    console.log('Clicked');
  }

  render() {
    return (
      <FacebookLogin
        appId={this.state.appId}
        autoLoad={true}
        fields="name,email,picture"
        //onClick={componentClicked}
        callback={responseFacebook} />
    )
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
})

export default connect(mapStateToProps, { facebookSignIn })(withRouter(FacebookSignIn));
