import React, { Component } from 'react'
import Chart from "chart.js";
import randomColor from '../randomColors';

export default class MyChart extends Component {
  state = {
    window_width: window.innerWidth
  }
  chartRef = React.createRef();

  componentDidMount() {
    const {window_width} = this.state;
    const {type, position} = this.props;

    window.addEventListener('resize', () => {
      this.setState({ window_width: window.innerWidth  })
    });

    const myChartRef = this.chartRef.current.getContext("2d");

    new Chart(myChartRef, {
      type: type,
      data: {
        //Bring in data
        labels: ["General", "Gas", "Bills", "Electric", "Tuition", "Cars", "House", "Stuff", "Kids", "Entertainment", "Hospitals"],
        datasets: [
          {
            data: [18, 27, 11, 10, 13, 9, 12, 22, 32, 42, 50],
            backgroundColor: randomColor(11)
          }
        ]
      },
      options: {
        //Customize chart options
        legend: {
          position: window_width > 945 ? (type === 'pie' || type ==='doughnut' ? 'right' : position) : 'bottom',
          labels: {
            generateLabels: function(chart) {
              var data = chart.data;
              if (data.labels.length && data.datasets.length) {
                return data.labels.map(function (label, i) {
                  return {
                    text: label + ': ' + data.datasets[0].data[i],
                    fillStyle: data.datasets[0].backgroundColor[i],
                    index: i
                  };
                });
              }
              return [];
            }
          }
        }
      }
    });
  }
  render() {
    return (
      <div>
        <canvas style={{width: '400vw', height: '250vh'}}
          id="myChart"
          ref={this.chartRef}
        />
      </div>
    )
  }
}
