import React, { Component } from 'react';
import {Table} from 'react-bootstrap';

class DataTable extends Component {
  state = {
    columns: ['Date', 'Amount', 'Category', 'Balance', 'Notes', 'Payee'],
    rows: [
      {
        date: 'April 15, 2019',
        amount: '$450',
        category: 'Electric bills',
        balance: '$5,000',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
      {
        date: 'Jan 15, 2019',
        amount: '$150',
        category: 'Gas bills',
        balance: '$5,000',
        notes: 'Short notes for description',
        payee: 'Tri Vo'
      },
      {
        date: 'Feb 15, 2019',
        amount: '$40',
        category: 'Water bills',
        balance: '$5,000',
        notes: 'Short notes for description',
        payee: 'Quang Vo'
      },
      {
        date: 'Aug 15, 2019',
        amount: '$450',
        category: 'Groceries',
        balance: '$5,000',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      }
    ]
  }
  render() {
    const {columns, rows} = this.state;
    return (
      <div>
        <Table striped bordered hover size="sm">
          <thead>
            <tr>
              {
                columns.map(column => {return (
                    <th>column</th>
                  )}
                )
              }
            </tr>
          </thead>

          <tbody>
            {
              rows.map(row => {
                return (
                  <tr>
                    <td>row.date</td>
                    <td>row.amount</td>
                    <td>row.category</td>
                    <td>row.balance</td>
                    <td>row.notes</td>
                    <td>row.payee</td>
                  </tr>
                )
              })
            }
          </tbody>
        </Table>
      </div>
    )
  }
}

export default DataTable;
