import React, { Component } from 'react';
import DataTable from './DataTable';
import {loadTransactions} from '../actions/transactionActions';
import {connect} from 'react-redux';

class AccountDetails extends Component {

  componentDidUpdate() {
    const {name, id} = this.props;
    if (id !== '' && id !== 'statistics' && id !== 'add-account' && id !== 'invest-calculator') {
      this.props.loadTransactions(id);
    }
  }

  onAdd_transaction = (e) => {
    e.preventDefault();
  }

  render() {
    const {name, transactions} = this.props;
    console.log(transactions);

    return (
      <div>
        <h2>
          <i className='fas fa-file-alt'>&nbsp;&nbsp;</i>Transactions history
        </h2><br/>
        <div className='d-flex'>
          <h4 className='mr-auto'>{name}</h4>
          <button className='btn btn-outline-secondary'><i className='fas fa-trash'></i>&nbsp;Delete</button>
        </div>
        <br/>

        <div className="table-hover table-responsive table-sm">
          <DataTable />
        </div>

      </div>
    )
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  accounts: state.accounts,
  transactions: state.transactions,
  success: state.success
})

export default connect(mapStateToProps, {loadTransactions})(AccountDetails);
