import React, { Component } from 'react';
import ReactTable from 'react-table';
import 'react-table/react-table.css';

export class TransactionsTable extends Component {
  state = {
    data: [
      {
        date: '2019/05/19',
        amount: 45.89,
        category: 'Bills',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
      {
        date: '2019/03/20',
        amount: 55.89,
        category: 'Gas',
        notes: 'Short notes for',
        payee: 'Trung Vo'
      },
      {
        date: '2019/05/20',
        amount: -5.89,
        category: 'Electric bills',
        notes: 'Short notes for description',
        payee: 'Trung T Vo'
      },
      {
        date: '2019/06/20',
        amount: 450.89,
        category: 'Tuition',
        notes: 'Paid June rent already, just reminder',
        payee: 'Trung Vo'
      },
      {
        date: '2019/01/20',
        amount: -445.89,
        category: 'General',
        notes: 'Short notes (general)',
        payee: 'Trung Vo'
      },
      {
        date: '2019/05/27',
        amount: 45000000.89,
        category: 'Cars payment',
        notes: 'Short notes',
        payee: 'Trung Vo'
      },
    ],
    columns: [
      {
        Header: 'Date',
        accessor: 'date',
        id: 'date',
        style: { textAlign: 'center', display: 'flex', flexDirection: 'column', justifyContent: 'center'},
      }, 
      {
        Header: 'Amount',
        accessor: 'amount',
        style: { 'whiteSpace': 'normal', flexDirection: 'column', justifyContent: 'center' },
        Cell: props => new Intl.NumberFormat('en-GB', { style: 'currency', currency: 'USD' }).format(props.value)

        // Cell: props =>
        //   <span 
        //     className='float-right'
        //     style={{ color: props.value >= 0 ? 'green' : 'red'}} 
        //   >
        //     {props.value >= 0 ? '+' + props.value : '-' + Math.abs(props.value)}
        //   </span>
      }, 
      {
        Header: 'Category',
        accessor: 'category',
        style: { 'whiteSpace': 'normal', textAlign: 'center', display: 'flex', flexDirection: 'column', justifyContent: 'center' },
        minWidth: 200,
        Cell: props =>
          <span className='p-2'>
            {props.value}
          </span>
      }, 
      {
        Header: 'Notes',
        accessor: 'notes',
        id: 'notes',
        style: { 'whiteSpace': 'normal', textAlign: 'center', display: 'flex', flexDirection: 'column', justifyContent: 'center' },
        minWidth: 250,
        Cell: props =>
        <span className='text-center'>
          {props.value}
        </span>
      }, 
      {
        Header: 'Payee',
        accessor: 'payee',
        minWidth: 180,
        style: { 'whiteSpace': 'normal', textAlign: 'center', display: 'flex', flexDirection: 'column', justifyContent: 'center' },
      }
    ]
  }
  render() {
    const {data, columns} = this.state;
    return (
      <ReactTable
        className="-striped -highlight"
        sorted={[{ 
          id: 'date',
          desc: true
        }]}
        data={data}
        columns={columns}
        defaultPageSize={5}
        pageSizeOptions={[5, 10, 15, 20]}
        defaultSorted={['date']}
      />

    )
  }
}

export default TransactionsTable;
