import React, { Component } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { InputGroup} from './InputGroup';

export class ModalAddEdit extends Component {
  state = {
    date: '',
    amount: '',
    category: '',
    notes: '',
    payee: ''
  }

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  render() {
    const { styles, header, buttonText, cancelBtn,
      mainFuncBtn, type, bankId, size, variant, disabled, icon } = this.props;

    return (
      <div>
        <Button variant={variant} className={styles} onClick={this.handleShow} size={size} disabled={disabled} >
          <i className='fas fa-trash'>&nbsp;&nbsp;</i>{buttonText}
        </Button>

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>
              <i className={icon}></i>
              <span>&nbsp;&nbsp;</span>{header}
            </Modal.Title>
          </Modal.Header>

          <Modal.Body>
            <InputGroup 
              htmlFor="date"
              label="Date"
              type="date"
              id="date"
              name="date"
              required={true}
              onChange={this.onChange}
              placeholder="Enter date" 
            />
            <InputGroup
              htmlFor="amount"
              label="Amount"
              type="amount"
              id="amount"
              name="amount"
              required={true}
              onChange={this.onChange}
              placeholder="Enter amount"
            />
            <InputGroup
              htmlFor="category"
              label="Category"
              type="category"
              id="category"
              name="category"
              required={true}
              onChange={this.onChange}
              placeholder="Enter category"
            />
            <InputGroup
              htmlFor="notes"
              label="Notes"
              type="notes"
              id="notes"
              name="notes"
              required={true}
              onChange={this.onChange}
              placeholder="Enter notes"
            />
            <InputGroup
              htmlFor="payee"
              label="Payee"
              type="payee"
              id="payee"
              name="payee"
              required={true}
              onChange={this.onChange}
              placeholder="Enter payee"
            />
          </Modal.Body>

          <Modal.Footer>
            <Form onSubmit={(e) => this.onDelete(e, type, bankId)}>
              <Button variant="primary" className="mr-2" type="submit" onClick={this.handleClose}>
                {mainFuncBtn}
              </Button>
              <Button variant="secondary" type="button" onClick={this.handleClose}>
                {cancelBtn}
              </Button>
            </Form>
          </Modal.Footer>
        </Modal>
      </div>
    )
  }
}

export default ModalAddEdit;
