import React, { Component } from 'react';
import DataTable from './DataTable';

class AccountDetails extends Component {

  onAdd_transaction = (e) => {
    e.preventDefault();
  }

  render() {
    const {name} = this.props;
    return (
      <div>
        <h2>
          <i className='fas fa-file-alt'>&nbsp;&nbsp;</i>Transactions history
        </h2><br/>
        <div className='d-flex'>
          <h4 className='mr-auto'>{name}</h4>
          <button className='btn btn-light'><i className='fas fa-trash'></i>&nbsp;Delete</button>
        </div>
        <br/>

        <div className="table-hover table-responsive table-sm">
          <DataTable />
        </div>

      </div>
    )
  }
}

export default AccountDetails;
