import React, { Component } from 'react';
import { connect } from 'react-redux';

class Dashboard extends Component {

  render() {

    return (
      <div>
        <div className="container mb-5">
          <h1 className="mt-4">Dashboard</h1>
          {
            this.props.auth.isAuthenticated ? 
            <div>
                <img src={this.props.auth.user.imageUrl !== '' ? this.props.auth.user.imageUrl : "/img/logo.png"}
                  alt="user-icon" id='profileImg'
                  width="100" height="100" className="img-thumbnail rounded-circle" /> <br /><br />

                <p className="lead text-dark">Edit information:</p>

                <form encType="multipart/form-data">
                  <input type="file" accept="image/*" name="photo" />
                  <input type="submit" value="upload" className="btn btn-outline-primary btn-sm" />
                </form> <br />

                <p className="lead text-dark mb-3">Welcome {this.props.auth.user.name}</p>
                <p className="lead text-dark mb-2">Email: {this.props.auth.user.email}</p><br />
            </div> :
            <div>
              <h2>Please sign in to view this!</h2>
              <a href="/users/login">Back to login</a>
            </div>
          }

        </div>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(mapStateToProps, null)(Dashboard);
