import React, { Component } from 'react';
import DataTable from './DataTable';
import DataTableSmall from './DataTableSmall';

class AccountDetails extends Component {
  onAdd_transaction = (e) => {
    e.preventDefault();
  }

  render() {
    return (
      <div>
        <h2>
          <i className='fas fa-file-alt'>&nbsp;&nbsp;</i>Transactions history
        </h2><br/>
        <h4>Citibank</h4><br/>

        <div id='transactions-table' 
          className="table-hover table-responsive table-sm text-left">
          <DataTable />
        </div>

        <div className="m-auto table-hover table-responsive table-sm text-right"
              id='transactions-table-small'>
          <DataTableSmall />
        </div>

      </div>
    )
  }
}

export default AccountDetails;
