import React, { Component } from 'react';
import DataTable from './DataTable';
import DataTableSmall from './DataTableSmall';

class AccountDetails extends Component {
  state = {
    hide: null
  }
  onAdd_transaction = (e) => {
    e.preventDefault();
  }

  componentDidMount() {
    window.addEventListener('resize', resize = () => {
      if (window.innerWidth <= 945) {
        this.setState({ hide: true })
      } else {
        this.setState({ hide: false })
      }
    });
  }

  render() {
    return (
      <div>
        <h2>
          <i className='fas fa-file-alt'>&nbsp;&nbsp;</i>Transactions history
        </h2><br/>
        <h4>Citibank</h4><br/>

        {
          !this.state.hide ? 
            <div id='transactions-table'
              className="table-hover table-responsive table-sm">
              <DataTable />
            </div> :
            <div className="m-auto table-hover table-responsive table-sm"
              id='transactions-table-small'>
              <DataTableSmall />
            </div>
        }
         {/* <div id='transactions-table' 
        //   className="table-hover table-responsive table-sm">
        //   <DataTable />
        // </div> */}

        {/* <div className="m-auto table-hover table-responsive table-sm"
               id='transactions-table-small'>
          <DataTableSmall />
        </div> */}

      </div>
    )
  }
}

export default AccountDetails;
