import React, { Component } from 'react';
import {connect} from 'react-redux';
import AddAccount from './AddAccount';
import {loadAccounts} from '../actions/accountActions';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import InputGroup from '../common/InputGroup';

class ManageAccounts extends Component {
  state = {
    tabIndex: 0
  }
  componentDidMount() {
    this.props.loadAccounts();
  }
  render() {
    const {accounts} = this.props;
    return (
      <div>
        <AddAccount /> <br/>
        <div className="container">
          <div className='card card-header'>
            <h4>
              <i className='fas fa-pen'>&nbsp;&nbsp;</i>Edit accounts
            </h4>
          </div>
          <div className='card card-body'>
            <Tabs defaultFocus={true} selectedIndex={this.state.tabIndex} onSelect={tabIndex => this.setState({ tabIndex })}>
              <TabList>
                {
                  accounts.accounts.map((account, i) =>
                    <Tab key={account._id}>{account.name}</Tab>
                  )
                }
              </TabList>
              {
                accounts.accounts.map((account, i) =>
                  <TabPanel key={account._id} >
                    <br />
                    <InputGroup htmlFor="name"
                      label="Name"
                      type="name"
                      id="name"
                      name="name"
                      required={true}
                      onChange={this.onChange}
                      placeholder="Enter account name" />


                    <div className="form-group row justify-content-start" style={{ 'display': 'flex', 'alignItems': 'center' }}>
                      <label htmlFor="currency" className="col-md-4 col-sm-6 col-xs-12 col-form-label">
                        Select currency: <span style={{ color: 'red' }}>*</span>
                      </label>
                      <div className="form-group col-md-7 col-sm-5 col-xs-12 mt-2">
                        <select className="custom-select" id='currency'
                          onChange={this.onChange}>
                          <option value="">Select currency</option>
                          <option value="USD">USD</option>
                          <option value="VND">VND</option>
                          <option value="EUR">EUR</option>
                        </select>
                      </div>
                    </div>

                    <InputGroup htmlFor="balance"
                      label="Balance"
                      type="balance"
                      id="balance"
                      name="balance"
                      required={true}
                      onChange={this.onChange}
                      placeholder="Enter account balance" />

                    <InputGroup htmlFor="budget"
                      label="Budget"
                      type="budget"
                      id="budget"
                      name="budget"
                      required={false}
                      onChange={this.onChange}
                      placeholder="Enter budget" />
                  </TabPanel>
                )
              }
            </Tabs>
          </div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  accounts: state.accounts
})

export default connect(mapStateToProps, { loadAccounts})(ManageAccounts);
