import React, { Component } from 'react';
import InputGroup from '../common/InputGroup';

export class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      password: '',
    }
  }
  componentDidMount() {
    console.log(this.props);
  }
  // handle login
  login = (e) => {
    e.preventDefault();

  }

  // save change when user type in their fields
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  render() {
    return (
      <div className="row mb-5 ml-auto mr-auto">
        <div className="col-md-6 m-auto">
          <img src="/img/logo.png" className="mx-auto d-block"
            id="appLogo" alt="logo" width="200" height="200" />

          <div className="card card-body">
            <h1 className="text-center mb-3"><i className="fas fa-sign-in-alt"></i> Login</h1>
              <form onSubmit={this.login} >
                <InputGroup htmlFor="email"
                  label="Email"
                  type="email"
                  id="email"
                  name="email"
                  onChange={this.onChange}
                  placeholder="Enter email address" />

                <InputGroup htmlFor="password"
                  label="Password"
                  type="password"
                  id="password"
                  name="password"
                  onChange={this.onChange}
                  placeholder="Enter password" />
                <button type="submit" id="btn-sign-in" className="btn btn-primary btn-block">Login</button>
              </form>
              <p className="lead mt-4">
                Don't have account? <a href="/users/register">Register</a>
              </p>
              <p className="lead">
                Forgot your password? <a href="/users/forgot">Reset</a>
              </p>
              <p>Or</p>

            <a href="/users/auth/google" id="google-button" 
                className="btn btn-block btn-google" data-onsuccess="onSignIn"
                data-theme="dark">
              <i className="fa fa-google-plus fa-fw"></i> Sign in with Google
            </a>
            <a href="#" id="facebook-button" className="btn btn-block btn-facebook">
              <i className="fa fa-facebook fa-fw"></i> Sign in with Facebook
            </a>
          </div>
        </div>
      </div>
    )
  }
}

export default Login;
